{
    "code": "org.meveo.service.script.InvoiceXmlScript",
    "description": "Invoice Xml Script",
    "type": "JAVA",
    "script": "package org.meveo.service.script;

import java.io.File;
import java.math.BigDecimal;
import java.text.DecimalFormat;
import java.text.DecimalFormatSymbols;
import java.util.ArrayList;
import java.util.Comparator;
import java.util.List;
import java.util.Locale;
import java.util.Map;
import java.util.stream.Collectors;

import javax.xml.xpath.XPath;
import javax.xml.xpath.XPathConstants;
import javax.xml.xpath.XPathExpression;
import javax.xml.xpath.XPathExpressionException;
import javax.xml.xpath.XPathFactory;

import org.meveo.admin.exception.BusinessException;
import org.meveo.commons.utils.ParamBean;
import org.meveo.commons.utils.StringUtils;
import org.meveo.model.billing.ChargeInstance;
import org.meveo.model.billing.Invoice;
import org.meveo.model.billing.OneShotChargeInstance;
import org.meveo.model.billing.RatedTransaction;
import org.meveo.model.billing.RecurringChargeInstance;
import org.meveo.model.billing.ServiceInstance;
import org.meveo.model.billing.Subscription;
import org.meveo.model.billing.UsageChargeInstance;
import org.meveo.model.billing.WalletOperation;
import org.meveo.model.catalog.OfferServiceTemplate;
import org.meveo.model.catalog.OfferTemplate;
import org.meveo.model.shared.DateUtils;
import org.meveo.service.billing.impl.RatedTransactionService;
import org.meveo.service.billing.impl.SubscriptionService;
import org.meveo.service.billing.impl.XMLInvoiceCreator;
import org.meveo.service.catalog.impl.OfferTemplateService;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.Node;
import org.w3c.dom.NodeList;

/**
 * 
 * @author Abdellatif BARI
 *
 */
public class InvoiceXmlScript extends org.meveo.service.script.Script {

    private static final Logger log = LoggerFactory.getLogger(InvoiceXmlScript.class);
    private OfferTemplateService offerTemplateService = (OfferTemplateService) getServiceInterface("OfferTemplateService");
    private RatedTransactionService ratedTransactionService = (RatedTransactionService) getServiceInterface("RatedTransactionService");
    private SubscriptionService subscriptionService = (SubscriptionService) getServiceInterface("SubscriptionService");
    private ParamBean paramBean = ParamBean.getInstance();

    /** default date format. */
    private static String DEFAULT_DATE_PATTERN = "dd/MM/yyyy";

    /**
     * Get the child element
     * 
     * @param parent the parent element
     * @param name the element name
     * @return the child element
     */
    private static Element getChild(Element parent, String name) {
        for (Node child = parent.getFirstChild(); child != null; child = child.getNextSibling()) {
            if (child instanceof Element && name.equals(child.getNodeName()))
                return (Element) child;
        }
        return null;
    }

    /**
     * label comparator
     * 
     * @param line1 the line element
     * @param line2 the line element
     * @return the result of comparator
     */
    private int labelComparator(Element line1, Element line2) {
        return getChild(line1, "label").getNodeValue().compareTo(getChild(line2, "label").getNodeValue());
    }

    /**
     * Get the iso 23 language
     * 
     * @param langIso3 the iso 3 language
     * @return the iso 23 language
     */
    private String getLangIso1FromLangIso23(String langIso3) {
        String[] languages = Locale.getISOLanguages();
        for (String language : languages) {
            Locale locale = new Locale(language);
            if (locale.getISO3Language().toUpperCase().equals(langIso3)) {
                return language;
            }
        }
        return null;
    }

    /**
     * format the amount
     * 
     * @param amount the amount
     * @param language the language
     * @return the string format of amount
     */
    private String format(BigDecimal amount, String language) {
        if (amount != null) {
            String patern = "###,##0.00";
            DecimalFormat df = null;
            if (StringUtils.isBlank(language)) {
                DecimalFormatSymbols formatSymbols = new DecimalFormatSymbols(new Locale(language));
                df = new DecimalFormat(patern, formatSymbols);
            } else {
                df = new DecimalFormat(patern);
            }
            return df.format(amount.doubleValue());
        }
        return null;
    }

    /**
     * Get the OfferServiceTemplate
     * 
     * @param ratedTransaction the rated transaction
     * @return the OfferServiceTemplate
     * @throws BusinessException the business exception
     */
    private OfferServiceTemplate getOfferServiceTemplate(RatedTransaction ratedTransaction) throws BusinessException {

        OfferTemplate offerTemplate = offerTemplateService.findByCode(ratedTransaction.getOfferCode());
        List<OfferServiceTemplate> offerServiceTemplates = offerTemplate.getOfferServiceTemplates();

        for (OfferServiceTemplate offerServiceTemplate : offerServiceTemplates) {
            if (offerServiceTemplate.getServiceTemplate().getServiceChargeTemplateSubscriptionByChargeCode(ratedTransaction.getCode()) != null) {
                return offerServiceTemplate;
            }
            if (offerServiceTemplate.getServiceTemplate().getServiceChargeTemplateTerminationByChargeCode(ratedTransaction.getCode()) != null) {
                return offerServiceTemplate;
            }
            if (offerServiceTemplate.getServiceTemplate().getServiceChargeTemplateUsageByChargeCode(ratedTransaction.getCode()) != null) {
                return offerServiceTemplate;
            }
            if (offerServiceTemplate.getServiceTemplate().getServiceRecurringChargeByChargeCode(ratedTransaction.getCode()) != null) {
                return offerServiceTemplate;
            }
        }

        return null;
    }

    /**
     * Get the ServiceInstance
     * 
     * @param ratedTransaction the rated transaction
     * @return the ServiceInstance
     * @throws BusinessException the business exception
     */
    private ServiceInstance getServiceInstance(RatedTransaction ratedTransaction) throws BusinessException {
        ServiceInstance serviceInstance = null;
        WalletOperation walletOperation = ratedTransaction.getWalletOperation();
        if (walletOperation != null) {
            // Retrieve Service Instance
            ChargeInstance chargeInstance = walletOperation.getChargeInstance();

            if (chargeInstance instanceof RecurringChargeInstance) {
                serviceInstance = ((RecurringChargeInstance) walletOperation.getChargeInstance()).getServiceInstance();
            } else if (chargeInstance instanceof UsageChargeInstance) {
                serviceInstance = ((UsageChargeInstance) walletOperation.getChargeInstance()).getServiceInstance();
            } else if (chargeInstance instanceof OneShotChargeInstance) {
                serviceInstance = ((OneShotChargeInstance) walletOperation.getChargeInstance()).getSubscriptionServiceInstance();
                if (serviceInstance == null) {
                    ((OneShotChargeInstance) walletOperation.getChargeInstance()).getTerminationServiceInstance();
                }
            }

        }
        return serviceInstance;
    }

    /**
     * find the rated transaction by code
     * 
     * @param code the rated transaction code
     * @returnthe rated transaction
     */
    private RatedTransaction findRatedTransactionByCode(String code) {
        List<RatedTransaction> ratedTransactions = ratedTransactionService.findByCodeLike(code);
        for (RatedTransaction rt : ratedTransactions) {
            if (rt.getCode().equals(code)) {
                return rt;
            }
        }
        return null;
    }

    /**
     * Map the line element
     * 
     * @param line the line element to map
     * @return the mapped line element
     * @throws BusinessException the business exception
     */
    private Element map(Element line) throws BusinessException {
        try {
            String invoiceDateFormat = paramBean.getProperty("invoice.dateFormat", DEFAULT_DATE_PATTERN);
            String code = line.getAttribute("code");
            RatedTransaction ratedTransaction = findRatedTransactionByCode(code);
            if (ratedTransaction != null) {
                OfferServiceTemplate offerServiceTemplate = null;
                offerServiceTemplate = getOfferServiceTemplate(ratedTransaction);
                if (offerServiceTemplate != null && offerServiceTemplate.isMandatory()) {
                    ServiceInstance serviceInstance = getServiceInstance(ratedTransaction);
                    if (serviceInstance != null) {
                        line.setAttribute("engagementStartDate", DateUtils.formatDateWithPattern(serviceInstance.getSubscriptionDate(), invoiceDateFormat));
                        line.setAttribute("engagementEndDate", DateUtils.formatDateWithPattern(serviceInstance.getTerminationDate(), invoiceDateFormat));
                    }
                }
            }
        } catch (BusinessException e) {
            log.error("map fail", e);
            throw new BusinessException(e.getMessage());
        }
        return line;
    }

    /**
     * Aggregate the SubCategory Lines.
     * 
     * @param document the xml document
     * @param xpath the xpath object
     * @param subCategory the subCategory element
     * @throws BusinessException the business exception
     */
    private void aggregateSubCategoryLines(Document document, XPath xpath, Element subCategory) throws BusinessException {

        String categoryCode = subCategory.getAttribute("code");
        try {
            XPathExpression recurringlinesExpression = xpath.compile("/invoice/detail/userAccounts/userAccount/categories/category/subCategories/subCategory[@code='" + categoryCode
                    + "']/line[string(@periodStartDate) and string(@periodEndDate)]");

            NodeList domLines = (NodeList) recurringlinesExpression.evaluate(document, XPathConstants.NODESET);
            String invoiceDateFormat = paramBean.getProperty("invoice.dateFormat", DEFAULT_DATE_PATTERN);

            if (domLines != null && domLines.getLength() > 0) {
                List<Element> lines = new ArrayList<>();
                for (int i = 0; i < domLines.getLength(); i++) {
                    lines.add((Element) domLines.item(i));
                }

                Comparator<Element> comparator1 = (line1, line2) -> DateUtils.parseDateWithPattern(line1.getAttribute("periodStartDate"), invoiceDateFormat)
                    .compareTo(DateUtils.parseDateWithPattern(line2.getAttribute("periodStartDate"), invoiceDateFormat));
                Comparator<Element> comparator2 = (line1, line2) -> DateUtils.parseDateWithPattern(line1.getAttribute("periodEndDate"), invoiceDateFormat)
                    .compareTo(DateUtils.parseDateWithPattern(line2.getAttribute("periodEndDate"), invoiceDateFormat));
                Comparator<Element> comparator3 = (line1, line2) -> labelComparator(line1, line2);

                // Grouping the list by periodEndDate
                List<Element> groupedLines = lines.stream().collect(Collectors.groupingBy(line -> line.getAttribute("periodEndDate"))).entrySet().stream().map(entry -> {
                    // Get line with the minimum periodStartDate
                    List<Element> linesEndDate = entry.getValue().stream().sorted(comparator1).collect(Collectors.toList());
                    return linesEndDate.get(0);
                }).collect(Collectors.toList());

                // Sorting the list by periodStartDate, periodEndDate and label, and add other attributes per line.
                try {
                    groupedLines = groupedLines.stream().sorted(comparator1.thenComparing(comparator2).thenComparing(comparator3)).map(line -> {
                        try {
                            map(line);
                        } catch (Exception e) {
                            throw new RuntimeException(e);
                        }
                        return line;
                    }).collect(Collectors.toList());
                } catch (RuntimeException e) {
                    throw (BusinessException) e.getCause();
                }
            }

        } catch (XPathExpressionException e) {
            log.error("aggregate fail", e);
            throw new BusinessException(e.getMessage());
        }
    }

    /**
     * Aggregate the SubCategory Lines.
     * 
     * @param document the xml document
     * @param xpath the xpath object
     * @throws BusinessException the business exception
     */
    private void aggregateSubCategoriesLines(Document document, XPath xpath) throws BusinessException {
        XPathExpression recurringSubCategoriesExpression;
        try {
            recurringSubCategoriesExpression = xpath
                .compile("/invoice/detail/userAccounts/userAccount/categories/category/subCategories/subCategory[line[string(@periodStartDate) and string(@periodEndDate)]]");

            NodeList domSubCategories = (NodeList) recurringSubCategoriesExpression.evaluate(document, XPathConstants.NODESET);

            if (domSubCategories != null && domSubCategories.getLength() > 0) {
                for (int i = 0; i < domSubCategories.getLength(); i++) {
                    Element subCategory = (Element) domSubCategories.item(i);
                    aggregateSubCategoryLines(document, xpath, subCategory);
                }
            }
        } catch (XPathExpressionException e) {
            log.error("aggregate fail", e);
            throw new BusinessException(e.getMessage());
        }
    }

    /**
     * Set the invoice period
     * 
     * @param document the xml document
     * @param nodeHeader the header node element
     * @param xpath the xpath object
     * @throws BusinessException the business exception
     */
    private void setInvoicePeriod(Document document, Node nodeHeader, XPath xpath) throws BusinessException {
        try {

            // get all recurring lines
            XPathExpression recurringlinesExpression = xpath
                .compile("/invoice/detail/userAccounts/userAccount/categories/category/subCategories/subCategory/line[string(@periodStartDate) and string(@periodEndDate)]");

            NodeList domLines = (NodeList) recurringlinesExpression.evaluate(document, XPathConstants.NODESET);
            String invoiceDateFormat = paramBean.getProperty("invoice.dateFormat", DEFAULT_DATE_PATTERN);

            if (domLines != null && domLines.getLength() > 0) {
                List<Element> lines = new ArrayList<>();
                for (int i = 0; i < domLines.getLength(); i++) {
                    lines.add((Element) domLines.item(i));
                }

                Comparator<Element> comparator1 = (line1, line2) -> DateUtils.parseDateWithPattern(line1.getAttribute("periodStartDate"), invoiceDateFormat)
                    .compareTo(DateUtils.parseDateWithPattern(line2.getAttribute("periodStartDate"), invoiceDateFormat));
                Comparator<Element> comparator2 = (line1, line2) -> DateUtils.parseDateWithPattern(line1.getAttribute("periodEndDate"), invoiceDateFormat)
                    .compareTo(DateUtils.parseDateWithPattern(line2.getAttribute("periodEndDate"), invoiceDateFormat));

                // Sorting the list by periodStartDate asc
                lines = lines.stream().sorted(comparator1).collect(Collectors.toList());

                // get the min periodStartDate --> periodStartDate invoice

                Element periodStartDateElement = document.createElement("periodStartDate");
                periodStartDateElement.setTextContent(lines.get(0).getAttribute("periodStartDate"));
                nodeHeader.appendChild(periodStartDateElement);

                // Sorting the list by periodEndDate desc
                lines = lines.stream().sorted(comparator2.reversed()).collect(Collectors.toList());

                // get the max periodEndDate --> periodEndDate invoice
                Element periodEndDateElement = document.createElement("periodEndDate");
                periodEndDateElement.setTextContent(lines.get(0).getAttribute("periodEndDate"));
                nodeHeader.appendChild(periodEndDateElement);

            }

        } catch (XPathExpressionException e) {
            log.error("set invoice period fail", e);
            throw new BusinessException(e.getMessage());
        }
    }

    /**
     * Set the subscriptions status
     * 
     * @param document the xml document
     * @param nodeHeader the header node element
     * @param xpath the xpath object
     * @throws BusinessException the business exception
     */
    private void setSubscriptionsStatus(Document document, Node nodeHeader, XPath xpath) throws BusinessException {
        XPathExpression subscriptionsExpression;
        try {
            subscriptionsExpression = xpath.compile("/invoice/detail/userAccounts/userAccount/subscriptions/subscription");

            NodeList domSubscriptions = (NodeList) subscriptionsExpression.evaluate(document, XPathConstants.NODESET);

            if (domSubscriptions != null && domSubscriptions.getLength() > 0) {
                for (int i = 0; i < domSubscriptions.getLength(); i++) {
                    Element subscription = (Element) domSubscriptions.item(i);
                    Long idSubscription = Long.valueOf(subscription.getAttribute("id"));
                    Subscription sub = subscriptionService.findById(idSubscription);
                    subscription.setAttribute("status", String.valueOf(sub.getStatus().getId()));
                }
            }
        } catch (XPathExpressionException e) {
            log.error("set subscriptions status fail", e);
            throw new BusinessException(e.getMessage());
        }
    }

    /**
     * Customize the xml document
     * 
     * @param invoice the invoice
     * @param isVirtual true or false
     * @param document the xml document
     * @throws BusinessException the business exception
     */
    public void customizeDocument(Invoice invoice, boolean isVirtual, Document document) throws BusinessException {
        try {

            String language = getLangIso1FromLangIso23(invoice.getBillingAccount().getTradingLanguage().getLanguageCode());
            if (StringUtils.isBlank(language)) {
                log.warn("Cant get language Iso1 code from :" + invoice.getBillingAccount().getTradingLanguage().getLanguageCode());
            }

            XPathFactory xpf = XPathFactory.newInstance();
            XPath xpath = xpf.newXPath();

            XPathExpression expressionHeader = xpath.compile("/invoice/header");
            Node nodeHeader = (Node) expressionHeader.evaluate(document, XPathConstants.NODE);

            // Invoicing Threshold
            Element invoicingThresholdElement = document.createElement("invoicingThreshold");
            invoicingThresholdElement.setTextContent(format(invoice.getBillingAccount().getBillingCycle().getInvoicingThreshold(), language));
            nodeHeader.appendChild(invoicingThresholdElement);

            // UnMatching Amount
            BigDecimal unMatchingAmount = null;
            if (invoice.getRecordedInvoice() != null && invoice.getRecordedInvoice().getUnMatchingAmount() != null) {
                unMatchingAmount = invoice.getRecordedInvoice().getUnMatchingAmount();
            }
            Element unMatchingAmountElement = document.createElement("unMatchingAmount");
            unMatchingAmountElement.setTextContent(format(unMatchingAmount, language));
            nodeHeader.appendChild(unMatchingAmountElement);

            // Set subscriptions status
            setSubscriptionsStatus(document, nodeHeader, xpath);

            // Set invoice period.
            setInvoicePeriod(document, nodeHeader, xpath);

            // aggregate SubCategories Lines
            aggregateSubCategoriesLines(document, xpath);

        } catch (Exception e) {
            log.error("customize XML Document fail", e);
            throw new BusinessException(e.getMessage());
        }

    }

    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {

        try {
            XMLInvoiceCreator xmlInvoiceCreator = (XMLInvoiceCreator) methodContext.get("XMLInvoiceCreator");
            Invoice invoice = (Invoice) methodContext.get(Script.CONTEXT_ENTITY);

            Boolean isVirtual = (Boolean) methodContext.get("isVirtual");
            Document document = xmlInvoiceCreator.createDocument(invoice, isVirtual.booleanValue());

            customizeDocument(invoice, isVirtual, document);

            File file = xmlInvoiceCreator.createFile(document, invoice);
            methodContext.put(Script.RESULT_VALUE, file);
        } catch (Exception e) {
            log.error("execute invoice XML script fail", e);
            throw new BusinessException(e.getMessage());
        }
    }
}",
    "executionRoles": [],
    "sourcingRoles": []
}

{
    "code": "org.meveo.service.script.SelectGatewayScript",
    "description": "SelectGatewayScript Script",
    "type": "JAVA",
    "script": "package org.meveo.service.script;

import org.meveo.admin.exception.BusinessException;
import org.meveo.commons.utils.StringUtils;
import org.meveo.model.payments.CustomerAccount;
import org.meveo.service.payments.impl.CustomerAccountService;

import java.util.Map;

/**
 *
 * @author Mounir Bahije
 *
 *         Script executed to Select Gateway
 *
 */
public class SelectGatewayScript extends Script {

    private CustomerAccountService customerAccountService = (CustomerAccountService) getServiceInterface("CustomerAccountService");

    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {
        CustomerAccount customerAccount = (CustomerAccount) methodContext.get(Script.CONTEXT_ENTITY);

        String pspCode = null;
        String codeSeller =null;

        if (customerAccount != null &&
                customerAccount.getBillingAccounts() != null &&
                customerAccount.getBillingAccounts().size() > 0 &&
                customerAccount.getBillingAccounts().get(0) != null &&
                customerAccount.getBillingAccounts().get(0).getUsersAccounts() !=null &&
                customerAccount.getBillingAccounts().get(0).getUsersAccounts().size() > 0 &&
                customerAccount.getBillingAccounts().get(0).getUsersAccounts().get(0) != null &&
                customerAccount.getBillingAccounts().get(0).getUsersAccounts().get(0).getSubscriptions() != null &&
                customerAccount.getBillingAccounts().get(0).getUsersAccounts().get(0).getSubscriptions().size() > 0 &&
                customerAccount.getBillingAccounts().get(0).getUsersAccounts().get(0).getSubscriptions().get(0) != null &&
                customerAccount.getBillingAccounts().get(0).getUsersAccounts().get(0).getSubscriptions().get(0).getSeller() != null &&
                customerAccount.getBillingAccounts().get(0).getUsersAccounts().get(0).getSubscriptions().get(0).getSeller().getSeller() != null &&
                !StringUtils.isBlank(customerAccount.getBillingAccounts().get(0).getUsersAccounts().get(0).getSubscriptions().get(0).getSeller().getSeller().getCode())
        ) {
            codeSeller = customerAccount.getBillingAccounts().get(0).getUsersAccounts().get(0).getSubscriptions().get(0).getSeller().getSeller().getCode();
        } else if (
                customerAccount != null &&
                        customerAccount.getCustomer() != null &&
                        customerAccount.getCustomer().getSeller() != null &&
                        customerAccount.getCustomer().getSeller().getSeller() != null &&
                        !StringUtils.isBlank(customerAccount.getCustomer().getSeller().getSeller().getCode())
        ) {
            codeSeller = customerAccount.getCustomer().getSeller().getSeller().getCode();
        }

        String codeSellerToTest = codeSeller;

         // "PRPH", "RELA", "PARI"
        if ("PRPH".equalsIgnoreCase(codeSellerToTest))  pspCode = "INGENICO_OGONE_CARD";
        if ("RELA".equalsIgnoreCase(codeSellerToTest))  pspCode = "INGENICO_OGONE_CARD";
        if ("PARI".equalsIgnoreCase(codeSellerToTest))  pspCode = "INGENICO_OGONE_CARD";
      
        // "FAC1", "CODI", "MONA"
        if ("FAC1".equalsIgnoreCase(codeSellerToTest))  pspCode = "INGENICO_OGONE_CARD3";
        if ("CODI".equalsIgnoreCase(codeSellerToTest))  pspCode = "INGENICO_OGONE_CARD3";
        if ("MONA".equalsIgnoreCase(codeSellerToTest))  pspCode = "INGENICO_OGONE_CARD3";

        if (codeSellerToTest == null) {
            methodContext.put("Status", "KO");
        }
        else {
            methodContext.put("Status", "OK");
            methodContext.put("RESULT_VALUE", pspCode);
        }
    }
}",
    "executionRoles": [],
    "sourcingRoles": []
}

{
