    "moduleItems": [
    	{
    		"script": {
                "code": "org.meveo.service.script.SaveIngenicoCardScript",
                "description": "Save Ingenico Card",
                "script": "package org.meveo.service.script;

import com.ingenico.connect.gateway.sdk.java.Client;
import com.ingenico.connect.gateway.sdk.java.RequestHeader;
import com.ingenico.connect.gateway.sdk.java.domain.payment.PaymentResponse;
import com.ingenico.connect.gateway.sdk.java.domain.payment.definitions.CardPaymentMethodSpecificOutput;
import com.ingenico.connect.gateway.sdk.java.domain.payment.definitions.PaymentOutput;
import com.ingenico.connect.gateway.sdk.java.domain.payment.definitions.PaymentReferences;
import com.ingenico.connect.gateway.sdk.java.domain.payment.definitions.PaymentStatusOutput;
import com.ingenico.connect.gateway.sdk.java.domain.token.TokenResponse;
import com.ingenico.connect.gateway.sdk.java.domain.webhooks.WebhooksEvent;
import com.ingenico.connect.gateway.sdk.java.webhooks.InMemorySecretKeyStore;
import com.ingenico.connect.gateway.sdk.java.webhooks.SignatureValidationException;
import com.ingenico.connect.gateway.sdk.java.webhooks.Webhooks;
import com.ingenico.connect.gateway.sdk.java.webhooks.WebhooksHelper;
import org.apache.commons.collections4.MapUtils;
import org.json.simple.JSONObject;
import org.json.simple.parser.JSONParser;
import org.json.simple.parser.ParseException;
import org.meveo.admin.exception.BusinessException;
import org.meveo.commons.utils.StringUtils;
import org.meveo.model.notification.InboundRequest;
import org.meveo.model.payments.CardPaymentMethod;
import org.meveo.model.payments.CreditCardTypeEnum;
import org.meveo.model.payments.CustomerAccount;
import org.meveo.model.payments.PaymentGateway;
import org.meveo.service.payments.impl.CustomerAccountService;
import org.meveo.service.payments.impl.PaymentGatewayService;
import org.meveo.service.payments.impl.PaymentMethodService;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 *
 * @author Mounir Bahije
 *
 *         Script executed to save card enrolled with Ingenico Hosted Checkout
 *
 */
public class SaveIngenicoCardScript extends Script {

    private final static Logger log = LoggerFactory.getLogger(SaveIngenicoCardScript.class);

    private PaymentMethodService paymentMethodService = (PaymentMethodService) getServiceInterface("PaymentMethodService");
    private CustomerAccountService customerAccountService = (CustomerAccountService) getServiceInterface("CustomerAccountService");
    private PaymentGatewayService paymentGatewayService = (PaymentGatewayService) getServiceInterface("PaymentGatewayService");

    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {

        String verification = "";
        InboundRequest inReq = (InboundRequest) methodContext.get("event");

        if ("GET".equalsIgnoreCase(inReq.getMethod())) {

            String body = inReq.getBody();
            Map<String, String> headers = inReq.getHeaders();
            for (String headerKey : headers.keySet()) {
                if ("X-GCS-Webhooks-Endpoint-Verification".equalsIgnoreCase(headerKey)) {
                    String headerValue = headers.get(headerKey);
                    verification = headerValue;
                    inReq.setResponseBody(verification);
                }
            }
        }

        if ("POST".equalsIgnoreCase(inReq.getMethod())) {

            final String reqBody = inReq.getBody();

            List<RequestHeader> requestHeaders = getWebhookRequestHeaders(inReq.getHeaders());
            String webhooksKeyId = this.getHeaderValue(requestHeaders, "X-GCS-KeyId");

            InMemorySecretKeyStore keyStore = InMemorySecretKeyStore.INSTANCE;
            keyStore.storeSecretKey(webhooksKeyId, getWebhookSecretKey(webhooksKeyId));

            WebhooksHelper helper = Webhooks.createHelper(keyStore);
            WebhooksEvent webhooksEvent = helper.unmarshal(reqBody, requestHeaders);

            PaymentResponse payment = webhooksEvent.getPayment();
            if (payment == null) {
                throw new BusinessException(String.format("PaymentResponse not found on webhooksEvent : [%s]", webhooksEvent));
            }

            PaymentOutput paymentOutput = payment.getPaymentOutput();
            if (paymentOutput == null) {
                throw new BusinessException(String.format("PaymentOutput not found on webhooksEvent.payment : [%s]", payment));
            }

            PaymentReferences paymentReferences = paymentOutput.getReferences();
            if (paymentReferences == null) {
                throw new BusinessException(String.format("paymentReferences not found on webhooksEvent.payment.paymentOutput : [%s]", paymentOutput));
            }

            String merchantReference = paymentReferences.getMerchantReference();

            if (merchantReference.lastIndexOf("_-_") >= 0) {

                String longIdCA = merchantReference.substring(merchantReference.lastIndexOf("_-_") + 3);
                CustomerAccount ca = customerAccountService.findById(Long.valueOf(longIdCA));

                PaymentGateway paymentGateway = paymentGatewayService.getPaymentGateway(ca, null, null);

                if (paymentGateway == null) {
                    throw new BusinessException("paymentGateway is null");
                }

                if (!webhooksKeyId.equals(paymentGateway.getWebhooksKeyId())) {
                    throw new BusinessException("paymentGateway is not the good one");
                }

                String status = payment.getStatus();
                if ("PENDING_CAPTURE".equalsIgnoreCase(status)) {
                    PaymentStatusOutput paymentStatusOutput = payment.getStatusOutput();
                    if (paymentStatusOutput == null) {
                        throw new BusinessException(String.format("paymentStatusOutput not found on webhooksEvent.payment : [%s]", payment));
                    }
                    Boolean isAuthorized = paymentStatusOutput.getIsAuthorized();

                    if (isAuthorized) {

                        CardPaymentMethodSpecificOutput cardPaymentMethodSpecificOutput = paymentOutput.getCardPaymentMethodSpecificOutput();
                        if (cardPaymentMethodSpecificOutput == null) {
                            throw new BusinessException(String.format("cardPaymentMethodSpecificOutput not found on webhooksEvent.payment.paymentOutput : [%s]", paymentOutput));
                        }
                        Long paymentProductId = Long.valueOf(cardPaymentMethodSpecificOutput.getPaymentProductId());

                        JSONParser jsonParser = new JSONParser();
                        JSONObject obj = null;
                        try {
                            obj = (JSONObject) jsonParser.parse(reqBody);
                        } catch (ParseException e) {
                            e.printStackTrace();
                        }

                        String tokenId = (String) ((JSONObject) ((JSONObject) ((JSONObject) obj.get("payment")).get("paymentOutput")).get("cardPaymentMethodSpecificOutput")).get("token");
                        if (StringUtils.isBlank(tokenId)) {
                            throw new BusinessException(String.format("tokenId not found on webhooksEvent.payment.paymentOutput.cardPaymentMethodSpecificOutput : [%s]", cardPaymentMethodSpecificOutput));
                        }

                        String merchantId = webhooksEvent.getMerchantId();

                        try {

                            CreditCardTypeEnum cardType = fromId(Long.valueOf(paymentProductId).intValue());

                            Client client = (Client) paymentMethodService.getClient(Long.valueOf(longIdCA));

                            TokenResponse response = client.merchant(merchantId).tokens().get(tokenId);

                            String cardNumber = response.getCard().getData().getCardWithoutCvv().getCardNumber();
                            String expiryDate = response.getCard().getData().getCardWithoutCvv().getExpiryDate();
                            String ownerName = response.getCard().getData().getCardWithoutCvv().getCardholderName();
                            CardPaymentMethod paymentMethod = new CardPaymentMethod();
                            paymentMethod.setCardNumber(cardNumber);
                            paymentMethod.setCardType(cardType);
                            paymentMethod.setCustomerAccount(ca);
                            paymentMethod.setPreferred(true);
                            paymentMethod.setMonthExpiration(new Integer(expiryDate.substring(0, 2)));
                            paymentMethod.setYearExpiration(new Integer(expiryDate.substring(2, 4)));
                            paymentMethod.setOwner(ownerName);
                            paymentMethod.setTokenId(tokenId);
                            paymentMethod.setHiddenCardNumber(CardPaymentMethod.hideCardNumber(cardNumber));
                            if (!paymentMethodService.cardTokenExist(paymentMethod)) {
                                paymentMethodService.create(paymentMethod);
                            }
                        } catch (Exception e) {
                            throw new BusinessException("Error on SaveIngenicoCardScript : " + e.getMessage());
                        }
                    }
                }
            }
        }
    }

    private CreditCardTypeEnum fromId(Integer id) {
        for (CreditCardTypeEnum es : CreditCardTypeEnum.values()) {
            if (es.getId().equals(id)) {
                return es;
            }
        }
        throw new IllegalArgumentException();
    }

    private List<RequestHeader> getWebhookRequestHeaders(Map<String, String> headers) {
        List<RequestHeader> webhookHeaders = new ArrayList<>();
        if (MapUtils.isNotEmpty(headers)) {
            headers.forEach((key, value) -> webhookHeaders.add(new RequestHeader(key, value)) );
        }
        return webhookHeaders;
    }

    private String getHeaderValue(List<RequestHeader> requestHeaders, String headerName) {
        String value = null;
        for (RequestHeader header : requestHeaders) {
            if (headerName.equalsIgnoreCase(header.getName())) {
                if (value == null) {
                    value = header.getValue();
                } else {
                    throw new SignatureValidationException("enocuntered multiple occurrences of header '" + headerName + "'");
                }
            }
        }
        if (value == null) {
            throw new SignatureValidationException("could not find header '" + headerName + "'");
        }
        return value;
    }

    private String getWebhookSecretKey(String webhooksKeyId) throws BusinessException {
        try {
            String query = "select pg.webhooksSecretKey from PaymentGateway pg where pg.webhooksKeyId=:webhooksKeyId";
            Map<String, Object> queryParams = new HashMap<>();
            queryParams.put("webhooksKeyId", webhooksKeyId);
            Object result = paymentGatewayService.executeSelectQuery(query, queryParams);
            return ((List<String>)result).get(0);
        } catch (Exception e) {
            throw new BusinessException(String.format("Error on getWebhookSecretKey for webhooksKeyId = [%s]", webhooksKeyId));
        }
    }
