import org.meveo.service.crm.impl.CustomFieldInstanceService;

public class AOFilterScript extends AccountOperationFilterScript {

    private static final String CF_RETARY_PAYMENT = "CF_RETARY_PAYMENT";
    private static final List<String> listRsnCodesToDontRetry = Arrays.asList("30941001", "30621001", "30591001", "30431001", "30051001", "30171001", "30071001", "30041001");

    @SuppressWarnings("unchecked")
    @Override
    public List<AccountOperation> filterAoToPay(Map<String, Object> methodContext) {
        log.debug(" filterAoToPay : [{}]", methodContext);
        List<AccountOperation> listAoToPay = (List<AccountOperation>) methodContext.get(AccountOperationFilterScript.LIST_AO_TO_PAY);
        if (CollectionUtils.isEmpty(listAoToPay)) {
            return listAoToPay;
        }

        List<AccountOperation> filtredListAoToPay = new ArrayList<>();
        for (AccountOperation ao : listAoToPay) {
            Subscription subscription = this.getSubscription(ao.getId());
            if (subscription != null && !"NO".equals(subscription.getCfValue(CF_RETARY_PAYMENT))) {
                filtredListAoToPay.add(ao);
            }
        }
        return filtredListAoToPay;
    }

    @SuppressWarnings("unchecked")
    @Override
    public void checkPaymentRetry(Map<String, Object> methodContext) {
        try {
            String errorCode = (String) methodContext.get("ERROR_CODE");
            if (StringUtils.isNotEmpty(errorCode) && listRsnCodesToDontRetry.contains(errorCode)) {
                List<Long> listAOids = (List<Long>) methodContext.get("LIST_AO_IDs");
                if (CollectionUtils.isNotEmpty(listAOids)) {
                    CustomFieldInstanceService customFieldInstanceService = (CustomFieldInstanceService) getServiceInterface(CustomFieldInstanceService.class.getSimpleName());
                    // All this AOs are supposed to belong to the same CA :
                    Subscription subscription = this.getSubscription(listAOids.get(0));
                    customFieldInstanceService.setCFValue(subscription, CF_RETARY_PAYMENT, "NO");
                }
            }
        } catch (Exception e) {
            log.error(" Error on checkPaymentRetry : [{}] ", e.getMessage(), e);
        }
    }

    @SuppressWarnings("unchecked")
    private Subscription getSubscription(Long aoId) {
        try { // For Fnac Dary : there is one invoice per AO , and one user account per ba and one subscription per UA :return
              // ao.getInvoices().get(0).getBillingAccount().getUsersAccounts().get(0).getSubscriptions().get(0);
            final SubscriptionService subscriptionService = (SubscriptionService) EjbUtils.getServiceInterface(SubscriptionService.class.getSimpleName());
            String queryOneSubscriptionFromAo = "select distinct s from Subscription s, AccountOperation ao  where ao.id =:aoId and s.userAccount.billingAccount.customerAccount = ao.customerAccount";
            Map<String, Object> queryParams = new HashMap<>();
            queryParams.put("aoId", aoId);
            Object objSubscription = subscriptionService.executeSelectQuery(queryOneSubscriptionFromAo, queryParams);

            return ((List<Subscription>) objSubscription).get(0);
        } catch (Exception e) {
            log.error(" Error on getSubscription : [{}] ", e.getMessage(), e);
            return null;
        }
    }
}
