    "code": "org.meveo.service.script.PaymentRetryStatusScript",
    "description": "PaymentRetryStatusScript",
    "type": "JAVA",
    "script": "package org.meveo.service.script;

import java.util.Arrays;
import java.util.List;
import java.util.Map;

import org.apache.commons.collections4.MapUtils;
import org.apache.commons.lang3.StringUtils;
import org.meveo.admin.exception.BusinessException;
import org.meveo.model.billing.Subscription;
import org.meveo.service.billing.impl.SubscriptionService;
import org.meveo.service.crm.impl.CustomFieldInstanceService;

public class PaymentRetryStatusScript extends Script {

    private static final List<String> retryValues = Arrays.asList("YES","NO");
    private static final String RETRY = "RETRY";
    private static final String CF_RETARY_PAYMENT = "CF_RETARY_PAYMENT";
    
    private final SubscriptionService subscriptionService = (SubscriptionService) getServiceInterface(SubscriptionService.class.getSimpleName());
    private final CustomFieldInstanceService customFieldInstanceService = (CustomFieldInstanceService) getServiceInterface(CustomFieldInstanceService.class.getSimpleName());

    private String retryPayment;
    
    @SuppressWarnings("unchecked")
    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {
        
        if (StringUtils.isEmpty(this.retryPayment) || !retryValues.contains(this.retryPayment)) {
            throw new BusinessException(String.format("Invalid parameter RETRY , allowed values are [%]", retryValues));  
        }
        Map<String, String> record = (Map<String, String>) methodContext.get("RECORD");
        if (record == null || MapUtils.isEmpty(record)) {
            throw new BusinessException(String.format("Parameter RECORD is required"));
        }
        String numContract = record.get("numContract");
        String recordRetry = record.get("retry");
        if (StringUtils.isNotEmpty(recordRetry) && retryValues.contains(recordRetry)) {
            this.changeRetryStatus(recordRetry, numContract); 
        } else {
            this.changeRetryStatus(this.retryPayment, numContract); 
        }
    }
    
    @Override
    public void init(Map<String, Object> methodContext) throws BusinessException {
        Object retry = methodContext.get(RETRY);
        if (retry == null) {
            throw new BusinessException(String.format("Parameter RETRY is required"));
        }
        String retryValue = (String) retry;
        if (!retryValues.contains(retryValue)) {
            throw new BusinessException(String.format("Invalid parameter RETRY , allowed values are [%s]", retryValues));  
        }
        this.retryPayment = retryValue;
    }

    private void changeRetryStatus(String retry, String numContract) throws BusinessException {
        try {
            if (StringUtils.isEmpty(numContract)) {
                throw new BusinessException(String.format(" ref contrat is required"));
            }
            Subscription subscription = subscriptionService.findByCode(numContract);
            if (subscription == null) {
                throw new BusinessException(String.format("Subscription having code = [%s] not found ", numContract));
            }
            customFieldInstanceService.setCFValue(subscription, CF_RETARY_PAYMENT, retry);
        } catch (Exception e) {
            log.error(" Error on changeRetryStatus [{}] ", e.getMessage());
            throw e;
        }
    }
}
