/**
 * A DDRequestBuilderScript Extension , aimed to generate SAGE SEPA File
 */
public class SageDDRequestBuilderScript extends DDRequestBuilderScript {

    private static final List<String> listRsnCodesToDontRetry = Arrays.asList("MS02", "MS03", "RC01", "RR0x", "SL01");

    /** code name of AO CF dedied to decide if unpaid invoice should be retried or not */
    private static final String CF_RETARY_PAYMENT = "CF_RETARY_PAYMENT";

    /** The Constant CHAR_TO_FILL_ZERO. */
    private static final char CHAR_TO_FILL_ZERO = '0';

    /** The Constant LINE_HEADER_CODE. */
    private static final String LINE_HEADER_CODE = "03";

    /** The Constant LINE_FOOTER_CODE. */
    private static final String LINE_FOOTER_CODE = "08";

    /** The Constant LINE_OPCODE. */
    private static final String LINE_OPCODE = "08";

    /** The Constant CURRENCY. */
    private static final String CURRENCY = "E";

    /** The Constant LINE_DETAIL_CODE. */
    private static final String LINE_DETAIL_CODE = "06";

    /** The Constant LINE_DETAIL2_CODE. */
    private static final String LINE_DETAIL2_CODE = "05";

    /** The Constant RECOGNITION_SIGN. */
    private static final String RECOGNITION_SIGN = "*";

    /** The Constant LINE_DETAIL2_SEQ. */
    private static final String LINE_DETAIL2_SEQ = "A";

    /** The Constant SCHEMA_TYPE_SDD. */
    private static final String SCHEMA_TYPE_SDD = "01";

    /** The Constant FORMAT_LANGAGE. */
    private static final String FORMAT_LANGAGE = "FR";

    private static final String ISSUER_BATCH_REF = "";
    // private static final String ISSUER_NATIONAL_NUM = "123456";
    private static final String ISSUER_INTERNAL_REF = "";

    /** The Constant ONE_HUNDRED. */
    private static final BigDecimal ONE_HUNDRED = new BigDecimal("100");

    private CustomFieldInstanceService customFieldInstanceService = (CustomFieldInstanceService) getServiceInterface(CustomFieldInstanceService.class.getSimpleName());

    @SuppressWarnings("unchecked")
    @Override
    public List<AccountOperation> findListAoToPay(Map<String, Object> methodContext) throws BusinessException {
        log.debug(" findListAoToPay : [{}]", methodContext);
        List<AccountOperation> listAoToPay = (List<AccountOperation>) methodContext.get(DDRequestBuilderScript.DD_REQUEST_LIST_AO);
        if (CollectionUtils.isEmpty(listAoToPay)) {
            return listAoToPay;
        }
        log.debug(" findListAoToPay : [{}]", methodContext);

        List<AccountOperation> filtredListAoToPay = new ArrayList<>();
        for (AccountOperation ao : listAoToPay) {
            Subscription subscription = this.getSubscription(ao);
            if (subscription != null && !"NO".equals(subscription.getCfValue(CF_RETARY_PAYMENT))) {
                filtredListAoToPay.add(ao);
            }
        }
        return filtredListAoToPay;
    }

    @Override
    public DDRejectFileInfos processDDRejectedFile(Map<String, Object> methodContext) throws BusinessException {

        DDRejectFileInfos ddRejectFileInfos = new DDRejectFileInfos();
        try {
            File sepaRejectedFile = (File) methodContext.get(DDRequestBuilderScript.DD_REJECT_FILE);
            if (sepaRejectedFile == null) {
                throw new BusinessException(" Sepa Rejected File not found !");
            }
            ddRejectFileInfos.setFileName(sepaRejectedFile.getName());
            Document rejectionDoc = (Document) JAXBUtils.unmarshaller(Document.class, sepaRejectedFile);

            List<AccountNotification2> listNtfctns = rejectionDoc.getBkToCstmrDbtCdtNtfctn().getNtfctn();
            for (AccountNotification2 acctNotification : listNtfctns) {
                for (ReportEntry2 entry : acctNotification.getNtry()) {
                    for (EntryDetails1 entryDetails1 : entry.getNtryDtls()) {
                        for (EntryTransaction2 entryTransaction2 : entryDetails1.getTxDtls()) {
                            TransactionReferences2 refs = entryTransaction2.getRefs();

                            if (refs != null) {
                                String instrId = refs.getInstrId();
                                log.debug(" processDDRejectedFile > instrId : [{}]", instrId);

                                Long ddRequestItemId = extractCorrelationId(instrId);
                                if (ddRequestItemId != null) {
                                    ReturnReasonInformation10 rtrInf = entryTransaction2.getRtrInf();
                                    if (rtrInf != null) {
                                        ReturnReason5Choice rsn = rtrInf.getRsn();
                                        if (rsn != null) {
                                            String rsnCode = rsn.getCd();
                                            if (listRsnCodesToDontRetry.contains(rsnCode)) {
                                                this.dontRetryPayments(ddRequestItemId);
                                            }
                                            ddRejectFileInfos.getListInvoiceRefsRejected().put(ddRequestItemId, rsnCode);
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        } catch (Exception e) {
            throw new BusinessException(" Error on processDDRejectedFile " + e.getMessage());
        }
        return ddRejectFileInfos;
    }

    private void dontRetryPayments(Long ddRequestItemId) {
        try {
            DDRequestItemService ddRequestItemService = (DDRequestItemService) getServiceInterface(DDRequestItemService.class.getSimpleName());
            DDRequestItem ddRequestItem = ddRequestItemService.findById(ddRequestItemId);
            for (AccountOperation ao : ddRequestItem.getAccountOperations()) {
                Subscription subscription = this.getSubscription(ao);
                if (subscription != null) {
                    customFieldInstanceService.setCFValue(subscription, CF_RETARY_PAYMENT, "NO");
                }
            }
        } catch (Exception e) {
            log.error(" Error on dontRetryPayments -> ddRequestItemId [{}] ({}) ", ddRequestItemId, e.getMessage());
        }
    }

    private Long extractCorrelationId(String instrId) {
        // instrId = <contractNum (having length = 12 > + <ddRequestLOT Id> , hence instrId should have length > 12
        if (StringUtils.isEmpty(instrId) || instrId.length() <= 12) {
            log.error(" Error on extractCorrelationId -> InstrId not found or has invalid length : [{}] ! ", instrId);
            return null;
        }
        try {
            return Long.valueOf(instrId.substring(12).trim());
        } catch (Exception e) {
            log.error(" Error on extractCorrelationId -> InstrId is not a valid Long value : [{}] ! ", instrId.substring(12));
            return null;
        }
    }

