    @Override
    public void generateDDRequestLotFile(Map<String, Object> methodContext) throws BusinessException {

        log.debug(" >>> Starting generateDDRequestLotFile ... ");

        Provider appProvider = (Provider) methodContext.get(DDRequestBuilderScript.PROVIDER);
        DDRequestLOT ddRequestLOT = (DDRequestLOT) methodContext.get(DDRequestBuilderScript.DD_REQUEST_LOT);

        // file name :
        String flatFilePath = ddRequestLOT.getFileName();
        if (StringUtils.isEmpty(flatFilePath)) {
            flatFilePath = this.sageFlatFilePath(ddRequestLOT, appProvider);
        }
        try (FileWriter fileWriter = new FileWriter(flatFilePath); BufferedWriter bufferedWriter = new BufferedWriter(fileWriter)) {

            List<DDRequestItem> dDRequestItems = ddRequestLOT.getDdrequestItems();
            if (CollectionUtils.isNotEmpty(dDRequestItems)) {
                int nbrItemHavingErrors = 0;
                Date prelevementDueDate = (Date) this.fetchCFConfigValue(ddRequestLOT.getDdRequestBuilder(), "CF_PRELEV_DUE_DATE", false);
                for (DDRequestItem ddRequestItem : dDRequestItems) {
                    if (!ddRequestItem.hasError()) {
                        this.addDetailLinesForRecordedInvoice(bufferedWriter, ddRequestItem, prelevementDueDate);
                    }
                    if (ddRequestItem.hasError()) {
                        nbrItemHavingErrors++;
                    }
                }
                // If all items have erros so throw exception
                if (nbrItemHavingErrors == dDRequestItems.size()) {
                    throw new Exception(" all DD Request Items have erros");
                }
            }

        } catch (Exception e) {
            String msg = e.getMessage();
            log.error(" error on generateDDRequestLotFile : [flatFilePath = {}] error =  [{}]", flatFilePath, msg, e);
            this.removeFileIfAlreadyCreated(flatFilePath);
            throw new BusinessException(msg, e);
        }
        log.debug(" [ End of generateDDRequestLotFile , generated file : [{}]", flatFilePath);
    }

    private void removeFileIfAlreadyCreated(String flatFilePath) {
        try {
            Files.deleteIfExists(Paths.get(flatFilePath));
        } catch (Exception e) {
            log.error(" error on removeFileIfAlreadyCreated [{}]", e.getMessage());
        }
    }

    private Long addDetailLinesForRecordedInvoice(BufferedWriter bufferedWriter, DDRequestItem ddRequestItem, Date prelevementDueDate) throws IOException {

        Long amountToPay = new Long(0);

        try {
            AccountOperation ao = ddRequestItem.getAccountOperations().get(0);
            final CustomerAccountService customerAccountService = (CustomerAccountService) getServiceInterface(CustomerAccountService.class.getSimpleName());
            CustomerAccount customerAccount = customerAccountService.retrieveIfNotManaged(ao.getCustomerAccount());
            PaymentMethod preferredMethod = customerAccount.getPreferredPaymentMethod();

            if (preferredMethod instanceof DDPaymentMethod) {

                // total amount of the ddRequestItem :
                BigDecimal ddRequestItemAmount = ddRequestItem.getAmount();

                if (ddRequestItemAmount.compareTo(BigDecimal.ZERO) != 0) {

                    // correlation identifier :
                    String correlationId = String.valueOf(ddRequestItem.getId());

                    DDPaymentMethod ddPaymentMethod = (DDPaymentMethod) preferredMethod;

                    String recipientName = getDefaultIfEmpty(getRecipientName(customerAccount), "");
                    String rum = getDefaultIfEmpty(ddPaymentMethod.getMandateIdentification(), "");

                    String mandatSignatureDate = DateUtils.formatDateWithPattern(ddPaymentMethod.getMandateDate(), "ddMMyy");
                    Subscription subscription = this.getSubscription(ao);

                    BankCoordinates bankCoordinates = ddPaymentMethod.getBankCoordinates();
                    // String domiciliation = getDefaultIfEmpty(bankCoordinates.getBankName(), "");
                    // #249 : use BIC instead of domiciliation
                    String bic = getDefaultIfEmpty(bankCoordinates.getBic(), "");
                    String contractNum = getDefaultIfEmpty(this.getContractNumber(subscription), "");
                    amountToPay = ddRequestItemAmount.setScale(2, RoundingMode.HALF_UP).multiply(ONE_HUNDRED).longValue();

                    // getting the Juridical Seller infos :
                    Seller seller = getJuridicalSeller(subscription);
                    SellerInfos sellerInfos = fetchSellerInfos(seller);

                    log.debug("Adding header line ...");
                    this.addFlatFileHeader(bufferedWriter, sellerInfos, prelevementDueDate);

                    String iban = bankCoordinates.getIban();
                    validateIbanSize(iban);

                    String recipientBankCode = iban.substring(4, 9);
                    String recipientBankBranchCode = iban.substring(9, 14);
                    String recipientBankAccountNum = iban.substring(14, 25);

                    log.debug("Adding detail line ...");
                    // adding detail line
                    String detail = FlatContentBuilder.newInstance().addItem(LINE_DETAIL_CODE).addItem(LINE_OPCODE).addBlank(8).addItem(sellerInfos.issuerNationalNum, 6)
                        .addItem(ISSUER_INTERNAL_REF, 12).addItem(recipientName, 24).addItem(bic, 24).addBlank(8).addItem(recipientBankBranchCode, 5)
                        .addItem(recipientBankAccountNum, 11).addItem(String.valueOf(amountToPay), false, 16, CHAR_TO_FILL_ZERO).addItem(RECOGNITION_SIGN).addItem(contractNum, 12)
                        .addItem(correlationId, 18).addItem(recipientBankCode, 5).addBlank(6).buid();

                    bufferedWriter.write(detail);
                    bufferedWriter.newLine();

                    log.debug("Adding detail2 line ...");
                    // adding detail2 line
                    String detail2 = FlatContentBuilder.newInstance().addItem(LINE_DETAIL2_CODE).addItem(LINE_DETAIL2_SEQ).addItem(rum, 35).addItem(SCHEMA_TYPE_SDD)
                        .addItem(mandatSignatureDate).addBlank(112).addItem(FORMAT_LANGAGE).buid();

                    bufferedWriter.write(detail2);
                    bufferedWriter.newLine();

                    log.debug("Adding footer line ...");
                    addFlatFileFooter(bufferedWriter, sellerInfos, amountToPay);

                } else {
                    String errorMsg = " ddRequestItemAmount should not be Zero ! ";
                    log.warn(" ddRequestItem id = [{}] , [{}]", ddRequestItem.getId(), errorMsg);
                    ddRequestItem.setErrorMsg(errorMsg);
                }
            } else {
                String errorMsg = "preferredMethod should be DDPaymentMethod";
                log.warn(" ddRequestItem id = [{}] , [{}]", ddRequestItem.getId(), errorMsg);
                ddRequestItem.setErrorMsg(errorMsg);
            }
        } catch (Exception e) {
            String msg = e.getMessage();
            log.error(" Error on addDetailLinesForRecordedInvoice : [{}] ", msg, e);
            ddRequestItem.setErrorMsg(msg);
        }
        return amountToPay;
    }

    private void validateIbanSize(String iban) throws BusinessException {
        if (StringUtils.isEmpty(iban) || iban.length() != 27) {
            throw new BusinessException(String.format(" Invalid iban [%s]", iban));
        }
    }

    /**
     * 
     * @param seller
     * @return the last 6 charachtes of the CF_S_FD_ICS CF value.
     * @throws BusinessException
     */
    private String getIssuerNationalNum(Seller seller) throws BusinessException {
        final String cfKey = "CF_S_FD_ICS";
        String ics = (String) this.fetchCFConfigValue(seller, cfKey, true);
        if (StringUtils.isNotEmpty(ics) && ics.length() >= 6) {
            return ics.substring(ics.length() - 6);
        } else {
            throw new BusinessException(String.format("Missing or invalid mandatory config [%s = %s]", cfKey, ics));
        }
    }

    private Seller getJuridicalSeller(Subscription subscription) throws BusinessException {
        try {
            Seller seller = subscription.getSeller().getSeller();
            if (seller == null) {
                throw new BusinessException(" Parent seller of the Subscription cannot be null,  [subscription id = {}]" + subscription.getId());
            }
            return seller;
        } catch (Exception e) {
            log.error(" Error on getJuridicalSeller : [{}] ", e.getMessage(), e);
            throw new BusinessException(" Cannot find Juridical Seller from Subscription [{}]" + subscription);
        }
    }

    private String getContractNumber(Subscription subscription) {
        try {
            return subscription.getCode();
        } catch (Exception e) {
            log.error(" Error on getContractNumber : [{}] ", e.getMessage(), e);
        }
        return null;
    }

    @SuppressWarnings("unchecked")
    private Subscription getSubscription(AccountOperation ao) {
        try { // For Fnac Dary : there is one invoice per AO , and one user account per ba and one subscription per UA :return
              // ao.getInvoices().get(0).getBillingAccount().getUsersAccounts().get(0).getSubscriptions().get(0);
            final SubscriptionService subscriptionService = (SubscriptionService) getServiceInterface(SubscriptionService.class.getSimpleName());
            String queryOneSubscriptionFromAo = "select distinct s from Subscription s, AccountOperation ao  where ao.id =:aoId and s.userAccount.billingAccount.customerAccount = ao.customerAccount";
            Map<String, Object> queryParams = new HashMap<>();
            queryParams.put("aoId", ao.getId());
            Object objSubscription = subscriptionService.executeSelectQuery(queryOneSubscriptionFromAo, queryParams);

            return ((List<Subscription>) objSubscription).get(0);
        } catch (Exception e) {
            log.error(" Error on getSubscription : [{}] ", e.getMessage(), e);
            return null;
        }
    }

    private void addFlatFileHeader(BufferedWriter bufferedWriter, SellerInfos sellerInfos, Date prelevementDueDate) throws IOException {
        if (prelevementDueDate == null) {
            prelevementDueDate = new Date();
        }
        String sDueDate = DateUtils.formatDDMMY(prelevementDueDate);

        String header = FlatContentBuilder.newInstance().addItem(LINE_HEADER_CODE).addItem(LINE_OPCODE).addBlank(8).addItem(sellerInfos.issuerNationalNum, 6).addBlank(7)
            .addItem(sDueDate).addItem(sellerInfos.issuerName, true, 24).addItem(ISSUER_BATCH_REF, 7).addBlank(19).addItem(CURRENCY).addBlank(5)
            .addItem(sellerInfos.issuerBankBranchCode, 5).addItem(sellerInfos.issuerBankAccountNum, 11).addBlank(47).addItem(sellerInfos.issuerBankCode, 5).addBlank(6).buid();

        bufferedWriter.write(header);
        bufferedWriter.newLine();
    }

    private void addFlatFileFooter(BufferedWriter bufferedWriter, SellerInfos sellerInfos, Long totalAmount) throws IOException {
        String opAmount = String.valueOf(totalAmount);
        String footer = FlatContentBuilder.newInstance().addItem(LINE_FOOTER_CODE).addItem(LINE_OPCODE).addBlank(8).addItem(sellerInfos.issuerNationalNum, 6).addBlank(84)
            .addItem(opAmount, false, 16, CHAR_TO_FILL_ZERO).addBlank(42).buid();

        bufferedWriter.write(footer);
        bufferedWriter.newLine();
    }

    /**
     * Sage flat file path.
     *
     * @param ddRequestLOT the dd request LOT
     * @param appProvider the app provider
     * @return the string
     */
    private String sageFlatFilePath(DDRequestLOT ddRequestLOT, Provider appProvider) {
        ParamBeanFactory paramBeanFactory = (ParamBeanFactory) getServiceInterface(ParamBeanFactory.class.getSimpleName());

        String fileName = ArConfig.getDDRequestFileNamePrefix() + ddRequestLOT.getId();
        fileName = fileName + "_" + appProvider.getCode();
        fileName = fileName + "_" + DateUtils.formatDateWithPattern(new Date(), "yyyyMMdd") + ArConfig.getDDRequestFileNameExtension();

        String outputDir = paramBeanFactory.getChrootDir();

        outputDir = outputDir + File.separator + ArConfig.getDDRequestOutputDirectory();
        outputDir = outputDir.replaceAll("\\..", "");

        log.info("DDRequest output directory=" + outputDir);
        File dir = new File(outputDir);
        if (!dir.exists()) {
            dir.mkdirs();
        }
        return outputDir + File.separator + fileName;
    }

    /**
     * Fetch sage pay batch config. (basically from Provider & CFs )
     *
     * @param appProvider the app provider
     * @param ddRequestLOT the dd request LOT
     * @return the sage pay config params
     * @throws BusinessException the business exception
     */
    private SellerInfos fetchSellerInfos(Seller seller) throws BusinessException {
        String issuerNationalNum = this.getIssuerNationalNum(seller);
        // String issuerName = getDefaultIfEmpty((String) this.fetchCFConfigValue(seller, "CF_S_FD_ISSUER_NAME", true), "");
        // => #249 :
        String issuerName = getDefaultIfEmpty(seller.getDescription(), "");
        String issuerBankBranchCode = getDefaultIfEmpty((String) this.fetchCFConfigValue(seller, "CF_S_FD_BRANCH_CODE", false), "");
        String issuerBankAccountNum = getDefaultIfEmpty((String) this.fetchCFConfigValue(seller, "CF_S_FD_ACCOUNT_NUMBER", false), "");
        String issuerBankCode = getDefaultIfEmpty((String) this.fetchCFConfigValue(seller, "CF_S_FD_BANK_CODE", false), "");
        return new SellerInfos(issuerNationalNum, issuerName, issuerBankBranchCode, issuerBankAccountNum, issuerBankCode);
    }

    /**
     * Fetch CF config value.
     *
     * @param entity the entity
     * @param cfKey the cf key
     * @param isMandatory the is mandatory
     * @return the string
     * @throws BusinessException the business exception
     */
    private Object fetchCFConfigValue(ICustomFieldEntity entity, String cfKey, boolean isMandatory) throws BusinessException {
        Object cfValue = customFieldInstanceService.getCFValue(entity, cfKey);
        if (isMandatory && (cfValue == null || (cfValue instanceof String && StringUtils.isEmpty(String.valueOf(cfValue))))) {
            throw new BusinessException(" Missing mandatory config [" + cfKey + "]");
        }
        return cfValue;
    }

    /**
     * Gets the recipient name.
     *
     * @param ca the ca
     * @return the recipient name
     */
    private String getRecipientName(CustomerAccount ca) {
        Name caName = ca.getName();
        if (caName == null) {
            log.warn(" Customer Account Name is null ! id =[{}] ", ca.getId());
            return "";
        } else {
            Title title = caName.getTitle();
            String firstName = getDefaultIfEmpty(caName.getFirstName(), "");
            String lastName = getDefaultIfEmpty(caName.getLastName(), "");
            return new StringBuffer(title != null ? title.getDescriptionNotNull() : "").append(" ").append(firstName).append(" ").append(lastName).toString();
        }
    }

    private class SellerInfos {

        String issuerNationalNum;
        String issuerName;
        String issuerBankBranchCode;
        String issuerBankAccountNum;
        String issuerBankCode;

        SellerInfos(String issuerNationalNum, String issuerName, String issuerBankBranchCode, String issuerBankAccountNum, String issuerBankCode) {
            this.issuerNationalNum = issuerNationalNum;
            this.issuerName = issuerName;
            this.issuerBankBranchCode = issuerBankBranchCode;
            this.issuerBankAccountNum = issuerBankAccountNum;
            this.issuerBankCode = issuerBankCode;
        }
    }
