import org.meveo.commons.utils.ParamBeanFactory;
import org.meveo.model.shared.DateUtils;
import org.meveo.service.billing.impl.SubscriptionService;

public class ExtractContractsToDontRetryScript extends Script {

    private final SubscriptionService subscriptionService = (SubscriptionService) getServiceInterface(SubscriptionService.class.getSimpleName());
    private final ParamBeanFactory paramBeanFactory = (ParamBeanFactory) getServiceInterface(ParamBeanFactory.class.getSimpleName());
    
    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {
        
        String outputDir =  String.valueOf(methodContext.get("OUTPUT_DIR"));
        outputDir = StringUtils.defaultIfEmpty(outputDir, "/tmp/subscriptions/dont_retry");
        outputDir = paramBeanFactory.getChrootDir() + File.separator + outputDir;
        
        File dir = new File(outputDir);
        if (!dir.exists()) {
            dir.mkdirs();
        }
        
        String flatFilePath = outputDir + File.separator + "dont_retry_subscriptions_" + DateUtils.formatDateWithPattern(new Date(), "yyyy-MM-dd HH:mm:ss.SSS") + ".txt"; 
        log.info("output flatFilePath=" + flatFilePath);
        this.findListSubscriptionsToToDontRetry(flatFilePath);
    }

    private void findListSubscriptionsToToDontRetry(String flatFilePath) throws BusinessException {

        log.debug(" Start findListSubscriptionsToToDontRetry ... ");
            
            int count = 0;
            try (FileWriter fileWriter = new FileWriter(flatFilePath); BufferedWriter bufferedWriter = new BufferedWriter(fileWriter)) {

                String query = "select s.code from billing_subscription s where s.cf_values\\:\\:json->'CF_RETARY_PAYMENT'->0->>'string' = 'NO'";
                Map<String, Object> params = new HashMap<>();
                List<Map<String, Object>> result = subscriptionService.executeNativeSelectQuery(query, params);
                if (CollectionUtils.isNotEmpty(result)) {
                  for (Map<String, Object> item : result) {
                      count++;
                      bufferedWriter.write(String.valueOf(item.get("code")));
                      bufferedWriter.newLine();
                  }
                } else {
                    throw new BusinessException("No Subscription found having Retry = 'NO'");
                }
            } catch (Exception e) {
                log.error(" error on findListSubscriptionsToToDontRetry  [{}]", e.getMessage());
                throw new BusinessException("error on findListSubscriptionsToToDontRetry : " + e.getMessage(), e);
            }
        log.debug(String.format(" End findListSubscriptionsToToDontRetry : [%d] items dound", count));
    }

}
