{
    "code": "org.meveo.service.script.CleanUpInactiveSubscriptionsScript",
    "description": "CurrentMonthDateRangeScript",
    "type": "JAVA",
    "script": "package org.meveo.service.script;

import java.util.ArrayList;
import java.util.Calendar;
import java.util.Collection;
import java.util.Collections;
import java.util.Date;
import java.util.HashMap;
import java.util.HashSet;
import java.util.List;
import java.util.Map;
import java.util.Set;

import org.apache.commons.collections4.CollectionUtils;
import org.meveo.admin.exception.BusinessException;
import org.meveo.model.billing.BillingAccount;
import org.meveo.model.billing.ChargeInstance;
import org.meveo.model.billing.Subscription;
import org.meveo.model.billing.UserAccount;
import org.meveo.model.crm.Customer;
import org.meveo.model.order.OrderItem;
import org.meveo.model.payments.CustomerAccount;
import org.meveo.service.billing.impl.ChargeInstanceService;
import org.meveo.service.billing.impl.SubscriptionService;
import org.meveo.service.crm.impl.CustomerService;
import org.meveo.service.order.OrderItemService;
import org.meveo.service.payments.impl.CustomerAccountService;

public class CleanUpInactiveSubscriptionsScript extends Script {

    private final SubscriptionService subscriptionService = (SubscriptionService) getServiceInterface(SubscriptionService.class.getSimpleName());
    private final CustomerAccountService customerAccountService = (CustomerAccountService) getServiceInterface(CustomerAccountService.class.getSimpleName());
    private final CustomerService customerService = (CustomerService) getServiceInterface(CustomerService.class.getSimpleName());
    private final OrderItemService orderItemService = (OrderItemService) getServiceInterface(OrderItemService.class.getSimpleName());
    private final ChargeInstanceService chargeInstanceService = (ChargeInstanceService) getServiceInterface(ChargeInstanceService.class.getSimpleName());

    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {

      
        String nbrDaysBefore = (String) methodContext.get("SINCE");
        
      
        Date validityDate = getValidityDate(nbrDaysBefore);
        Set<Long> listCustomerIds = new HashSet<>();

        // removing C / CA having no Subscriptions
        List<CustomerAccount> listCAsHavingNoSubscription = findCAsHavingNoSubscription(validityDate);
        if (listCAsHavingNoSubscription == null) {
            listCAsHavingNoSubscription = new ArrayList<>();
        }

        Set<CustomerAccount> listCAsToRemove = new HashSet<>(listCAsHavingNoSubscription);

        // Removing C / CA having no Active Subscriptions
        List<Subscription> listSubscriptionIdsToRemove = findListSubscriptionsToRemove(validityDate);
        for (Subscription s : safe(listSubscriptionIdsToRemove) ) {
            CustomerAccount ca = s.getUserAccount().getBillingAccount().getCustomerAccount();
            if (!uaHasOrderItems(ca)) { // TODO : ask for confirmation of => this.removeUAsOrderItems(ca);
                this.removeChargeInstances(s);
                listCAsToRemove.add(ca); // For FD we have one Subscription per CustomerAccount , so removing the Subscription should lead to remove its CustomerAccount also
                listCustomerIds.add(ca.getCustomer().getId());
            }
        }

        for (CustomerAccount ca : safe (listCAsToRemove) ) {
            listCustomerIds.add(ca.getCustomer().getId());
            ca.getCustomer().getCustomerAccounts().remove(ca);
            try {
                log.debug(" >>> removing ca [id={}] ", ca.getId());
                // TODO : ask for confirmation of => this.removeUAsOrderItems(ca);
                customerAccountService.remove(ca); // we use this remove method instead of customerAccountService.remove(listCAsIds) in order to benefit from CascadeType.REMOVE
            } catch (Exception e) {
                log.error("Error on customerAccountService.remove(ca) [{}] ,  [{}] ", ca, e.getMessage(), e);
            }
        }

        // removing empty Customers
        this.removeCustomerIfEmpty(listCustomerIds);

    }

    @SuppressWarnings("unchecked")
    private void removeChargeInstances(Subscription subscription) {
        try {
            String query = "select ci from ChargeInstance ci where ci.subscription =:subscription";
            Map<String, Object> queryParams = new HashMap<>();
            queryParams.put("subscription", subscription);
            List<ChargeInstance> ids = (List<ChargeInstance>) orderItemService.executeSelectQuery(query, queryParams);
            
            if (CollectionUtils.isNotEmpty(ids)) {
                // chargeInstanceService.remove(new HashSet<>(ids)); // in order to benefit from CascadeType.REMOVE
                for (ChargeInstance ci : ids) {
                    chargeInstanceService.remove(ci); 
                }
            }
        } catch (Exception e) {
            log.error("Error on removeChargeInstances  [Subscription={}] ,  [{}] ", subscription, e.getMessage(), e);
        }
    }

    @SuppressWarnings("unchecked")
    private boolean uaHasOrderItems(CustomerAccount ca) {
        Set<UserAccount> listUAs = new HashSet<>();
        try {
            for (BillingAccount ba : safe(ca.getBillingAccounts())) {
                for (UserAccount ua : safe(ba.getUsersAccounts())) {
                    listUAs.add(ua);
                }
            }

            String query = "select oi.id from OrderItem oi where oi.userAccount in :listUAs";
            Map<String, Object> queryParams = new HashMap<>();
            queryParams.put("listUAs", listUAs);

            List<Long> listOrderItemIds = (List<Long>) orderItemService.executeSelectQuery(query, queryParams);
            return CollectionUtils.isNotEmpty(listOrderItemIds);
        } catch (Exception e) {
            log.error("Error on removeUAsOrderItems for [ca={}] ,  [{}] ", ca, e.getMessage(), e);
            return true;
        }

    }

    @Deprecated
    @SuppressWarnings("unchecked")
    private void removeUAsOrderItems(CustomerAccount ca) {
        Set<UserAccount> listUAs = new HashSet<>();
        try {
            for (BillingAccount ba : safe(ca.getBillingAccounts())) {
                for (UserAccount ua : safe(ba.getUsersAccounts())) {
                    listUAs.add(ua);
                }
            }

            String query = "select oi from OrderItem oi where oi.userAccount in :listUAs";
            Map<String, Object> queryParams = new HashMap<>();
            queryParams.put("listUAs", listUAs);

            List<OrderItem> listOrderItemIds = (List<OrderItem>) orderItemService.executeSelectQuery(query, queryParams);
            for (OrderItem orderItem : safe(listOrderItemIds)) {
                orderItemService.remove(orderItem);
            }
        } catch (Exception e) {
            log.error("Error on removeUAsOrderItems for [ca={}] ,  [{}] ", ca, e.getMessage(), e);
        }
    }

    @SuppressWarnings("unchecked")
    private <T> Collection<T> safe(Collection<T> collection) {
        return collection == null ? Collections.EMPTY_LIST : collection;
    }

    private Date getValidityDate(String nbrDaysBefore) {
      
        Long nbrDays = null;
        try {
             nbrDays = new Long(nbrDaysBefore);
        } catch (Exception e) {
            nbrDays = new Long(0);
        }
           
        log.debug(" >>>>>>>>>>>>> " + nbrDaysBefore);
        Calendar validityCal = Calendar.getInstance();
        validityCal.set(Calendar.HOUR_OF_DAY, 0);
        validityCal.set(Calendar.MINUTE, 0);
        validityCal.set(Calendar.SECOND, 0);
        validityCal.add(Calendar.DAY_OF_MONTH, nbrDays.intValue());
        Date validityDate = validityCal.getTime();
        return validityDate;
    }

    private List<Subscription> findListSubscriptionsToRemove(Date validityDate) {

        log.debug(" findListSubscriptionsToRemove -> validityDate = [{}] ", validityDate);

        List<Subscription> subscriptionHavingInactiveServices = this.findSubscriptionHavingInactiveServices(validityDate);
        List<Subscription> listSubscriptionIdsToRemove = new ArrayList<>();

        if (CollectionUtils.isNotEmpty(subscriptionHavingInactiveServices)) {
            for (Subscription s : subscriptionHavingInactiveServices) {
                if (this.subscriptionHasNoActiveServices(s)) {
                    listSubscriptionIdsToRemove.add(s);
                }
            }
        }
        log.debug(" Found [{}] Subscription to remove", listSubscriptionIdsToRemove.size());
        return listSubscriptionIdsToRemove;
    }

    private void removeCustomerIfEmpty(Set<Long> listCustomerIds) {
        log.debug(" >>>>>>>>>>>>> removing empty Customers " + listCustomerIds);
        if (CollectionUtils.isNotEmpty(listCustomerIds)) {
            for (Long id : listCustomerIds) {
                try {
                    log.debug(" >>>>>>>>>>>>> removing Customer id = " + id);
                    Customer c = customerService.findById(id);
                    log.debug(" >>>>>>>>>>>>> removing Customer getCustomerAccounts IS  EMPTY ? = " + CollectionUtils.isEmpty(c.getCustomerAccounts()));
                    if (CollectionUtils.isEmpty(c.getCustomerAccounts())) {
                        log.debug(" >>>>>>>>>>>>> removing Customer getCustomerAccounts isEmpty = " + id);
                        customerService.remove(id);
                    }
                } catch (BusinessException e) {
                    log.error("Error on removeCustomerIfEmpty [{}] ", e.getMessage());
                }
            }
        }
    }

    @SuppressWarnings("unchecked")
    private List<CustomerAccount> findCAsHavingNoSubscription(Date validityDate) {

        try {
   StringBuffer query = new StringBuffer("select DISTINCT ca from CustomerAccount ca LEFT JOIN ca.billingAccounts ba LEFT JOIN ba.usersAccounts ua LEFT JOIN ua.subscriptions s ")
                .append(" where ca.dateStatus < :validityDate and ( ca.billingAccounts IS EMPTY OR ba.usersAccounts IS EMPTY OR ua.subscriptions IS EMPTY OR s.serviceInstances IS EMPTY)");

          
          Map<String, Object> queryParams = new HashMap<>();
            queryParams.put("validityDate", validityDate);

            Object result = customerAccountService.executeSelectQuery(query.toString(), queryParams);
            return (List<CustomerAccount>) result;
        } catch (Exception e) {
            log.error(" Error on findCAsHavingNoSubscription : [{}] ", e.getMessage(), e);
            return null;
        }
    }

    @SuppressWarnings("unchecked")
    private boolean subscriptionHasNoActiveServices(Subscription s) {
        String query = "select si from ServiceInstance si where si.status = 'ACTIVE' and si.subscription =:subscription";
        Map<String, Object> queryParams = new HashMap<>();
        queryParams.put("subscription", s);
        Object objSubscription = subscriptionService.executeSelectQuery(query, queryParams);
        return objSubscription == null || CollectionUtils.isEmpty((List<Subscription>) objSubscription);
    }

    @SuppressWarnings("unchecked")
    private List<Subscription> findSubscriptionHavingInactiveServices(Date validityDate) {
        try {
            StringBuffer query = new StringBuffer("select DISTINCT s from Subscription s, CustomerAccount ca ")
                .append(" LEFT JOIN s.serviceInstances service where s.userAccount.billingAccount.customerAccount = ca ")
                .append(" and ca.dateStatus < :validityDate  and service.status != 'ACTIVE'");

            Map<String, Object> queryParams = new HashMap<>();
            queryParams.put("validityDate", validityDate);
            Object objSubscription = subscriptionService.executeSelectQuery(query.toString(), queryParams);

            return ((List<Subscription>) objSubscription);
        } catch (Exception e) {
            log.error(" Error on getSubscription : [{}] ", e.getMessage(), e);
            return null;
        }
    }
}
