import org.meveo.service.script.Script;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

/**
 * The Class ExportComptableScript.
 *
 * @author anasseh
 * 
 *         Execute Context Variable : -fileNamePrefix : optional variable default = ExportComptable_<Event>_<fromDate>_<toDate>.csv -outputDir :relative path from provider
 *         directory, optional variable default = <empty>
 */
public class ExportComptableScript extends Script {

    /** The Constant log. */
    private static final Logger log = LoggerFactory.getLogger(ExportComptableScript.class);

    /** The account operation service. */
    private AccountOperationService accountOperationService = (AccountOperationService) getServiceInterface("AccountOperationService");

    /** The payment service. */
    private PaymentService paymentService = (PaymentService) getServiceInterface("PaymentService");

    /** The invoice agregate service. */
    private InvoiceAgregateService invoiceAgregateService = (InvoiceAgregateService) getServiceInterface("InvoiceAgregateService");

    /** The code facture. */
    private final String codeFacture = "INV_STD";

    /** The code avoir. */
    private final String codeAvoir = "INV_CRN";

    /** The code payment CB. */
    private final String codePaymentCB = "PAY_CRD";

    /** The code payment DD. */
    private final String codePaymentDD = "PAY_DDT";

    /** The code refund CB. */
    private final String codeRefundCB = "REF_CRD";

    /** The code refund DD. */
    private final String codeRefundDD = "REF_DDT";

    /** The code refund CH. */
    private final String codeRefundCH = "REF_CHK";

    /** The code reject payment CB. */
    private final String codeRejectPaymentCB = "REJ_CRD";

    /** The code reject refund CB. */
    private final String codeRejectRefundCB = "REJ_RCR";

    /** The code event cycle facturation. */
    private final String codeEventCycleFacturation = "EF1";

    /** The code event cycle financement facturation. */
    private final String codeEventCycleFinancementFacturation = "EP1";

    /** The code event gestion banque. */
    private final String codeEventGestionBanque = "EI1";

    /** The code event cloture fin mois. */
    private final String codeEventClotureFinMois = "EC1";

    /** The code event perte creances. */
    private final String codeEventPerteCreances = "EA1";

    /** The code journal ventes encaissements. */
    private final String codeJournalVentesEncaissements = "FAC1";

    /** The code journal remboursements decaissements. */
    private final String codeJournalRemboursementsDecaissements = "AVO1";

    /** The code journal financement encaissements. */
    private final String codeJournalFinancementEncaissements = "FIN1";

    /** The code journal remboursement client C B SEPA. */
    private final String codeJournalRemboursementClientCB_SEPA = "RBA1";

    /** The code journal remboursement client cheque. */
    private final String codeJournalRemboursementClientCheque = "RBC1";

    /** The code journal constatation impaye rejet. */
    private final String codeJournalConstatationImpayeRejet = "BQI1";

    /** The code journal representation impaye. */
    private final String codeJournalRepresentationImpaye = "BQR1";

    /** The code journal C A produits constates avance. */
    private final String codeJournalCA_ProduitsConstatesAvance = "PCA1";

    /** The code journal C A pacture emettre. */
    private final String codeJournalCA_PactureEmettre = "FAE1";

    /** The code journal abandon creances. */
    private final String codeJournalAbandonCreances = "ABC1";

    /** The code journal annulation creances. */
    private final String codeJournalAnnulationCreances = "ANC1";

    private Date fromDate = null;
    private Date toDate = null;
    private String fileNamePrefix = null;
    private String outputDir = null;
    private JobInstance job = null;

    @Override
    public void execute(Map<String, Object> context) throws BusinessException {

        job = (JobInstance) context.get(Script.CONTEXT_ENTITY);
        fromDate = (Date) job.getCfValue("FROM_DATE");
        toDate = (Date) job.getCfValue("TO_DATE");
        fileNamePrefix = (String) context.get("fileNamePrefix");
        outputDir = (String) context.get("outputDir");
        Date now = new Date();
        Calendar calendar = Calendar.getInstance();
        if (fromDate == null) {
            if (codeEventGestionBanque.equals((String) job.getCfValue("TYPE_EVENT"))) {
                fromDate = DateUtils.addDaysToDate(now, -1);
            } else {
                calendar.set(DateUtils.getYearFromDate(now), DateUtils.getMonthFromDate(now), 1);
                fromDate = calendar.getTime();
            }
        }

        if (toDate == null) {
            if (codeEventGestionBanque.equals((String) job.getCfValue("TYPE_EVENT"))) {
                toDate = DateUtils.addDaysToDate(now, -1);
            } else {
                calendar.set(Calendar.DAY_OF_MONTH, calendar.getActualMaximum(Calendar.DAY_OF_MONTH));
                toDate = calendar.getTime();
            }
        }
        fromDate = DateUtils.setDateToStartOfDay(fromDate);
        toDate = DateUtils.setDateToEndOfDay(toDate);
        if (StringUtils.isBlank(fileNamePrefix)) {
            fileNamePrefix = "ExportComptable_";
        }
        log.debug("procesing ExportComptable for fromDate:{}, toDate:{}, fileNamePrefix:{}, outputDir:{}", fromDate, toDate, fileNamePrefix, outputDir);
        if (StringUtils.isBlank(fileNamePrefix)) {
            fileNamePrefix = "ExportComptable_";
        }
        if (codeEventCycleFacturation.equals((String) job.getCfValue("TYPE_EVENT"))) {
            eventCycleFacturation(fileNamePrefix + "Vente_", outputDir, fromDate, toDate);
        }
        if (codeEventCycleFinancementFacturation.equals((String) job.getCfValue("TYPE_EVENT"))) {
            eventCycleFinancementFacturation(fileNamePrefix + "Finance_", outputDir, fromDate, toDate);
        }
        if (codeEventGestionBanque.equals((String) job.getCfValue("TYPE_EVENT"))) {
            eventGestionBanque(fileNamePrefix + "Banque_", outputDir, fromDate, toDate);
        }
    }

    /**
     * Event cycle facturation.
     *
     * @param fileNamePrefix the file name prefix
     * @param outputDir the output dir
     * @param fromDate the from date
     * @param toDate the to date
     * @throws BusinessException the business exception
     */
    private void eventCycleFacturation(String fileNamePrefix, String outputDir, Date fromDate, Date toDate) throws BusinessException {
        List<String[]> lineVentes = new ArrayList<String[]>();
        lineVentes.addAll(getLinesEventCycleFacturation(codeFacture, codeEventCycleFacturation, codeJournalVentesEncaissements, fromDate, toDate));
        lineVentes.addAll(getLinesEventCycleFacturation(codeAvoir, codeEventCycleFacturation, codeJournalRemboursementsDecaissements, fromDate, toDate));
        generateFile(lineVentes, outputDir, fromDate, toDate, fileNamePrefix);
    }

    /**
     * Gets the lines event cycle facturation.
     *
     * @param occCode the occ code
     * @param event the event
     * @param journal the journal
     * @param from the from
     * @param to the to
     * @return the lines event cycle facturation
     * @throws BusinessException the business exception
     */
    @SuppressWarnings("unchecked")
    private List<String[]> getLinesEventCycleFacturation(String occCode, String event, String journal, Date from, Date to) throws BusinessException {
        List<String[]> listEvent = new ArrayList<String[]>();
        List<RecordedInvoice> listAOs = (List<RecordedInvoice>) listEvent(occCode, from, to, null);
        log.info("listAOs occCode:{}, event:{}, journal:{},from:{}, to:{}, size:{}", occCode, event, journal, from, to, (listAOs == null ? "null" : listAOs.size()));
        for (RecordedInvoice ao : listAOs) {
            log.info("The invoice:{}", ao.getInvoices().get(0));
            List<TaxInvoiceAgregate> cats = (List<TaxInvoiceAgregate>) invoiceAgregateService.listByInvoiceAndType(ao.getInvoices().get(0), "T");
            log.info("List TaxInvoiceAgregate size:{}", (cats == null ? "null" : cats.size()));
            for (TaxInvoiceAgregate taxInvoiceAgregate : cats) {
                listEvent.add(theMethode(event, journal, ao, null, "M_ABO", taxInvoiceAgregate.getAmountWithoutTax(), taxInvoiceAgregate.getTaxPercent()));
                listEvent.add(theMethode(event, journal, ao, null, "M_TVA", taxInvoiceAgregate.getAmountTax(), taxInvoiceAgregate.getTaxPercent()));
            }
            listEvent.add(theMethode(event, journal, ao, null, "M_TTC", ao.getAmount(), null));
        }
        return listEvent;
    }

    /**
     * Event cycle financement facturation.
     *
     * @param fileNamePrefix the file name prefix
     * @param outputDir the output dir
     * @param fromDate the from date
     * @param toDate the to date
     * @throws BusinessException the business exception
     */
    private void eventCycleFinancementFacturation(String fileNamePrefix, String outputDir, Date fromDate, Date toDate) throws BusinessException {
        List<String[]> lineFinances = new ArrayList<String[]>();
        lineFinances.addAll(getLinesEventCycleFinancementFacturation(codePaymentCB, codeEventCycleFinancementFacturation, codeJournalFinancementEncaissements, fromDate, toDate));
        lineFinances.addAll(getLinesEventCycleFinancementFacturation(codePaymentDD, codeEventCycleFinancementFacturation, codeJournalFinancementEncaissements, fromDate, toDate));
        lineFinances.addAll(getLinesEventCycleFinancementFacturation(codeRefundCB, codeEventCycleFinancementFacturation, codeJournalRemboursementClientCB_SEPA, fromDate, toDate));
        lineFinances.addAll(getLinesEventCycleFinancementFacturation(codeRefundDD, codeEventCycleFinancementFacturation, codeJournalRemboursementClientCB_SEPA, fromDate, toDate));
        generateFile(lineFinances, outputDir, fromDate, toDate, fileNamePrefix);
    }

    /**
     * Gets the lines event cycle financement facturation.
     *
     * @param occCode the occ code
     * @param event the event
     * @param journal the journal
     * @param from the from
     * @param to the to
     * @return the lines event cycle financement facturation
     * @throws BusinessException the business exception
     */
    @SuppressWarnings("unchecked")
    private List<String[]> getLinesEventCycleFinancementFacturation(String occCode, String event, String journal, Date from, Date to) throws BusinessException {
        List<String[]> listEvent = new ArrayList<String[]>();
        List<AccountOperation> listAOs = (List<AccountOperation>) listEvent(occCode, from, to, MatchingStatusEnum.L);
        log.info("listAOs occCode:{}, event:{}, journal:{},from:{}, to:{}, size:{}", occCode, event, journal, from, to, (listAOs == null ? "null" : listAOs.size()));
        for (AccountOperation accountOperation : listAOs) {
            AccountOperation invoiceThatWasPaid = getInvoiceThatWasPaid(accountOperation);
            if (invoiceThatWasPaid == null) {
                throw new BusinessException("The payment/refund AO id:" + accountOperation.getId() + " are matched with a no invoice/adjustment");
            }
            if (invoiceThatWasPaid instanceof RecordedInvoice) {
                listEvent.add(theMethode(event, journal, accountOperation, (RecordedInvoice) invoiceThatWasPaid, "M_TTC", accountOperation.getAmount(), null));
            }
        }
        return listEvent;
    }

    /**
     * Event gestion banque.
     *
     * @param fileNamePrefix the file name prefix
     * @param outputDir the output dir
     * @param fromDate the from date
     * @param toDate the to date
     * @throws BusinessException the business exception
     */
    private void eventGestionBanque(String fileNamePrefix, String outputDir, Date fromDate, Date toDate) throws BusinessException {
        List<String[]> linesBanque = new ArrayList<String[]>();
        linesBanque.addAll(getLinesEventGestionBanque(codeRefundCH, codeEventGestionBanque, codeJournalRemboursementClientCheque, fromDate, toDate, MatchingStatusEnum.L));
        linesBanque.addAll(getLinesEventGestionBanque(codePaymentDD, codeEventGestionBanque, codeJournalConstatationImpayeRejet, fromDate, toDate, MatchingStatusEnum.R));
        linesBanque.addAll(getLinesEventGestionBanque(codeRefundDD, codeEventGestionBanque, codeJournalConstatationImpayeRejet, fromDate, toDate, MatchingStatusEnum.R));
        linesBanque.addAll(getLinesEventGestionBanque(codeRejectPaymentCB, codeEventGestionBanque, codeJournalConstatationImpayeRejet, fromDate, toDate, MatchingStatusEnum.L));
        linesBanque.addAll(getLinesEventGestionBanque(codeRejectRefundCB, codeEventGestionBanque, codeJournalConstatationImpayeRejet, fromDate, toDate, MatchingStatusEnum.L));

        generateFile(linesBanque, outputDir, fromDate, toDate, fileNamePrefix);
    }

    /**
     * Gets the lines event gestion banque.
     *
     * @param occCode the occ code
     * @param event the event
     * @param journal the journal
     * @param fromDate the from date
     * @param toDate the to date
     * @param matchingStatusEnum the matching status enum
     * @return the lines event gestion banque
     * @throws BusinessException the business exception
     */
    @SuppressWarnings("unchecked")
    private List<String[]> getLinesEventGestionBanque(String occCode, String event, String journal, Date fromDate, Date toDate, MatchingStatusEnum matchingStatusEnum)
            throws BusinessException {
        List<String[]> listEvent = new ArrayList<String[]>();
        AccountOperation invoiceThatWasPaid = null;
        List<AccountOperation> listAOs = (List<AccountOperation>) listEvent(occCode, fromDate, toDate, matchingStatusEnum);
        log.info("listAOs occCode:{}, event:{}, journal:{},from:{}, to:{}, matchingStatusEnum:{}, size:{}", occCode, event, journal, fromDate, toDate, matchingStatusEnum,
            (listAOs == null ? "null" : listAOs.size()));
        for (AccountOperation accountOperation : listAOs) {
            if (journal.equals(codeJournalRemboursementClientCheque)) {
                invoiceThatWasPaid = getInvoiceThatWasPaid(accountOperation);
                if (invoiceThatWasPaid == null) {
                    throw new BusinessException("The payment check AO id:" + accountOperation.getId() + " are matched with a no invoice");
                }
                listEvent.add(theMethode(event, journal, accountOperation, invoiceThatWasPaid, "M_TTC", accountOperation.getAmount(), null));
            }

            if (journal.equals(codeJournalConstatationImpayeRejet)) {
                if (accountOperation.getPaymentMethod() == PaymentMethodEnum.DIRECTDEBIT) {
                    invoiceThatWasPaid = getInvoiceThatShouldBePaidByDD(accountOperation);
                }
                if (accountOperation.getPaymentMethod() == PaymentMethodEnum.CARD) {
                    invoiceThatWasPaid = getInvoiceThatShouldBePaidByCC(accountOperation);
                }
                if (invoiceThatWasPaid == null) {
                    throw new BusinessException("Cant find the invoice/refund that supposed  paid by the AO id :" + accountOperation.getId());
                }
                listEvent.add(theMethode(event, journal, accountOperation, invoiceThatWasPaid, "M_TTC", accountOperation.getAmount(), null));
            }

        }
        return listEvent;
    }

    /**
     * Gets the invoice that should be paid by DD.
     *
     * @param accountOperation the account operation
     * @return the invoice that should be paid by DD
     */
    @SuppressWarnings("unchecked")
    private RecordedInvoice getInvoiceThatShouldBePaidByDD(AccountOperation accountOperation) {
        if (accountOperation.getDdRequestItem() == null) {
            return null;
        }
        List<RecordedInvoice> listAOs = new ArrayList<RecordedInvoice>();
        Map<String, Object> params = new HashMap<String, Object>();
        params.put("ddReqItemId", accountOperation.getDdRequestItem().getId());

        String queryStr = "Select ri from RecordedInvoice as ri  where ri.ddRequestItem.id =:ddReqItemId";

        listAOs = (List<RecordedInvoice>) accountOperationService.executeSelectQuery(queryStr, params);
        if (listAOs != null && !listAOs.isEmpty()) {
            return listAOs.get(0);
        }

        return null;
    }

    /**
     * Gets the invoice that should be paid by CC.
     *
     * @param accountOperation the account operation
     * @return the invoice that should be paid by CC
     */
    @SuppressWarnings("unchecked")
    private RecordedInvoice getInvoiceThatShouldBePaidByCC(AccountOperation accountOperation) {
        List<RecordedInvoice> listAOs = new ArrayList<RecordedInvoice>();
        Map<String, Object> params = new HashMap<String, Object>();
        params.put("rejectedPaymentId", accountOperation.getId());

        String queryStr = "Select ri from RecordedInvoice as ri where ri.rejectedPayment.id =:rejectedPaymentId";
        //String queryStr = "Select ri from RecordedInvoice as ri  where ri.paymentInfo6 =:rejectedPaymentId";

        listAOs = (List<RecordedInvoice>) accountOperationService.executeSelectQuery(queryStr, params);
        if (listAOs != null && !listAOs.isEmpty()) {
            return listAOs.get(0);
        }

        return null;
    }

    /**
     * Gets the invoice that was paid.
     *
     * @param accountOperation the account operation
     * @return the invoice that was paid
     * @throws BusinessException the business exception
     */
    private AccountOperation getInvoiceThatWasPaid(AccountOperation accountOperation) throws BusinessException {
        if (accountOperation.getMatchingStatus() != MatchingStatusEnum.L) {
            return null;
        }
        List<? extends AccountOperation> listAos = null;
        if (accountOperation.getMatchingAmounts() != null || !accountOperation.getMatchingAmounts().isEmpty()) {
           listAos = paymentService.getAccountOperationThatWasPaid(accountOperation);            
            if (listAos == null || listAos.isEmpty()) {
                return null;
            }
        }
        return listAos.get(0);

    }

    /**
     * The methode.
     *
     * @param event the event
     * @param journal the journal
     * @param accountOperation the account operation
     * @param linkedAccountOperation the linked account operation
     * @param saleType the sale type
     * @param amount the amount
     * @param taxPercent the tax percent
     * @return the string[]
     * @throws BusinessException the business exception
     */
    private String[] theMethode(String event, String journal, AccountOperation accountOperation, AccountOperation linkedAccountOperation, String saleType, BigDecimal amount,
            BigDecimal taxPercent) throws BusinessException {
        String[] t = new String[33];
        Subscription subscription = getSubscription(accountOperation);
        Seller seller = subscription.getSeller();
        OfferTemplate offer = subscription.getOffer();
        t[0] = format(event, 3);
        t[1] = format(journal, 3);
        t[2] = DateUtils.formatDateWithPattern(getDateOperation(accountOperation, journal), "yyyyMMdd");
        if (seller == null || seller.getSeller() == null || seller.getSeller().getSeller() == null) {
            throw new BusinessException("A seller is null");
        }
        t[3] = "FNAC".equals(seller.getSeller().getSeller().getCode()) ? "F" : "D";
        t[4] = format(seller.getSeller().getCode(), 4);// Sociétés Juridiques
        t[5] = format(seller.getCode(), 6);// magasin
        t[6] = format((String) seller.getCfValue("CF_S_FD_SELLER_TYPE"), 1);// type magasin
        t[7] = format(subscription.getCode(), 50);// Contrats
        t[8] = format(accountOperation.getCustomerAccount().getName() == null ? "" : accountOperation.getCustomerAccount().getName().getLastName(), 35); // Nom Client
        t[9] = format(accountOperation.getCustomerAccount().getName() == null ? "" : accountOperation.getCustomerAccount().getName().getFirstName(), 35);// Prenom client
        t[10] = format((String) offer.getCfValue("CF_OF_FD_ANALYTIC_OFFER_TYPE"), 3);// type offer
        t[11] = format((String) subscription.getCfValue("CF_SU_FD_OFFER_CODE"), 40);// code offer metier
        t[12] = format(offer.getName(), 256);// libelle offer
        t[13] = format((String) offer.getCfValue("CF_OF_FD_ANALYTIC_BUSINESS_SECTOR"), 3);// SecteurCommercial
        t[14] = format(((String) offer.getCfValue("CF_OF_FD_ANALYTIC_OFFER_PERIODICITY")).equals("Annuelle") ? "A" : "M", 1);// Fréquence
        t[15] = format((String) offer.getCfValue("CF_OF_FD_ANALYTIC_OFFER_PERIODICITY"), 20);// libelle Fréquence
        t[16] = format(((String) offer.getCfValue("CF_OF_FD_ANALYTIC_REVENUE_TYPE")).equals("CA") ? "O" : "N", 1);// type CA// type CA
        t[17] = DateUtils.formatDateWithPattern(subscription.getSubscriptionDate(), "yyyyMMdd");// Date de souscription du contrat
        t[18] = format(getInvoiceNumber(event, accountOperation, linkedAccountOperation), 50);// N° Facture
        t[19] = DateUtils.formatDateWithPattern(getInvoiceDate(event, accountOperation, linkedAccountOperation), "yyyyMMdd");// Date de codeFacture
        t[20] = saleType;// Sale type
        t[21] = "+";// signe
        t[22] = format(getSensComptable(journal, saleType), 1);
        t[23] = format(round(amount, 2), 13);// Montant
        t[24] = taxPercent == null ? format("", 4) : StringUtils.getLongAsNChar(taxPercent.multiply(new BigDecimal(100)).longValue(), 4);// Taux TVA
        t[25] = format("", 2);// type remise //TODO wating core
        t[26] = format(getBusinessPaymentMethod(accountOperation, journal, "NO".equals((String) subscription.getCfValue("CF_RETARY_PAYMENT"))), 3);// Mode de paiement
        t[27] = format(getCodeRemise(accountOperation, event), 13);// getCodeRemiseBanque(ao);//un id lot unique sur 7 (13 sur la spec 8) char //TODO waiting core
        String[] address = getAddress(accountOperation, journal);
        t[28] = format(address[0], 35);// Adresse – rue 1
        t[29] = format(address[1], 35);// Adresse – rue 2
        t[30] = format(address[2], 10);// Adresse – code postal
        t[31] = format(address[3], 35);// Adresse – Ville
        t[32] = format(address[4], 3);// Adresse – code pays

        return t;

    }

    /**
     * Gets the address.
     *
     * @param accountOperation the account operation
     * @param journal the journal
     * @return the address
     */
    private String[] getAddress(AccountOperation accountOperation, String journal) {
        String[] address = { "", "", "", "", "" };
        if (journal.equals(codeJournalRemboursementClientCheque)) {
            if (accountOperation.getCustomerAccount().getAddress() != null) {
                address[0] = accountOperation.getCustomerAccount().getAddress().getAddress1();
                address[1] = accountOperation.getCustomerAccount().getAddress().getAddress2();
                address[2] = accountOperation.getCustomerAccount().getAddress().getZipCode();
                address[3] = accountOperation.getCustomerAccount().getAddress().getCity();
                if (accountOperation.getCustomerAccount().getAddress().getCountry().getCountryCode() != null) {
                    address[4] = accountOperation.getCustomerAccount().getAddress().getCountry().getCountryCode();
                }

            }
        }
        return address;
    }

    /**
     * Gets the invoice date.
     *
     * @param event the event
     * @param accountOperation the account operation
     * @param linkedAccountOperation the linked account operation
     * @return the invoice date
     */
    private Date getInvoiceDate(String event, AccountOperation accountOperation, AccountOperation linkedAccountOperation) {
        if (event.equals(codeEventCycleFacturation)) {
            return accountOperation.getTransactionDate();
        }

        return linkedAccountOperation.getTransactionDate();
    }

    /**
     * Gets the invoice number.
     *
     * @param event the event
     * @param accountOperation the account operation
     * @param linkedAccountOperation the linked account operation
     * @return the invoice number
     */
    private String getInvoiceNumber(String event, AccountOperation accountOperation, AccountOperation linkedAccountOperation) {
        if (event.equals(codeEventCycleFacturation)) {
            return accountOperation.getReference();
        }

        return linkedAccountOperation.getReference();

    }

    /**
     * Gets the date operation.
     *
     * @param accountOperation the account operation
     * @param journal the journal
     * @return the date operation
     */
    private Date getDateOperation(AccountOperation accountOperation, String journal) {
        if (journal.equals(codeJournalCA_PactureEmettre)) {
            return null;// TODO : Le dernier jour du mois M-1
        }
        if (journal.equals(codeJournalCA_ProduitsConstatesAvance)) {
            return null;// TODO : Le dernier jour du mois M
        }

        return accountOperation.getTransactionDate();
    }

    /**
     * Gets the subscription.
     *
     * @param ao the ao
     * @return the subscription
     */
    private Subscription getSubscription(AccountOperation ao) {

        if (ao.getCustomerAccount() != null && ao.getCustomerAccount().getBillingAccounts() != null && !ao.getCustomerAccount().getBillingAccounts().isEmpty()) {
            BillingAccount ba = ao.getCustomerAccount().getBillingAccounts().get(0);
            if (ba != null && ba.getUsersAccounts() != null && !ba.getUsersAccounts().isEmpty()) {
                UserAccount ua = ba.getUsersAccounts().get(0);
                if (ua != null && ua.getSubscriptions() != null && !ua.getSubscriptions().isEmpty()) {
                    return ua.getSubscriptions().get(0);
                }

            }

        }
        return null;
    }

    /**
     * List event.
     *
     * @param occCode the occ code
     * @param from the from
     * @param to the to
     * @param matchingStatusEnum the matching status enum
     * @return the list<? extends account operation>
     */
    @SuppressWarnings("unchecked")
    private List<? extends AccountOperation> listEvent(String occCode, Date from, Date to, MatchingStatusEnum matchingStatusEnum) {
        @SuppressWarnings("rawtypes")
        List<? extends AccountOperation> listAOs = new ArrayList();
        Map<String, Object> params = new HashMap<String, Object>();
        params.put("fromDate", from);
        params.put("toDate", to);
        params.put("occCode", occCode);
        if (matchingStatusEnum != null) {
            params.put("matchingStatusEnum", matchingStatusEnum);
        }

        String queryStr = "Select ao from AccountOperation as ao  where ao.transactionDate >=:fromDate and ao.transactionDate <:toDate and  " + "ao.occCode =:occCode ";

        if (codeRejectPaymentCB.equals(occCode) || codeRejectRefundCB.equals(occCode)) {
            queryStr = "Select ao from AccountOperation as ao  where ao.rejectedDate >=:fromDate and ao.rejectedDate <:toDate and  " + "ao.occCode =:occCode ";
        }
        if (matchingStatusEnum != null) {
            queryStr += " and ao.matchingStatus =:matchingStatusEnum";
        }
        listAOs = (List<? extends AccountOperation>) accountOperationService.executeSelectQuery(queryStr, params);
        log.debug("Count occCode:{} occCodelistAOs :{}", occCode, listAOs == null ? null : listAOs.size());

        return listAOs;
    }

    /**
     * Round.
     *
     * @param amount the amount
     * @param scale the scale
     * @return the string
     */
    private String round(BigDecimal amount, Integer scale) {
        if (amount == null) {
            amount = BigDecimal.ZERO;
        }
        if (scale == null) {
            scale = 2;
        }
        amount = amount.setScale(scale, RoundingMode.HALF_UP);
        return getAmountAsNChar(amount.toPlainString().replaceAll("\\.", ","), 13);
    }

    /**
     * Format.
     *
     * @param field the field
     * @param length the length
     * @return the string
     */
    private String format(String field, int length) {

        return StringUtils.getStringAsNChar(field, length);
    }

    /**
     * Gets the sens comptable.
     *
     * @param journal the journal
     * @param saleType the sale type
     * @return the sens comptable
     */
    private String getSensComptable(String journal, String saleType) {
        if (codeJournalVentesEncaissements.equals(journal) && "M_ABO".equals(saleType))
            return "C";
        if (codeJournalVentesEncaissements.equals(journal) && "M_TVA".equals(saleType))
            return "C";
        if (codeJournalVentesEncaissements.equals(journal) && "M_TTC".equals(saleType))
            return "D";
        if (codeJournalVentesEncaissements.equals(journal) && "M_REM".equals(saleType))
            return "D";
        if (codeJournalVentesEncaissements.equals(journal) && "M_FRA".equals(saleType))
            return "C";

        if (codeJournalRemboursementsDecaissements.equals(journal) && "M_ABO".equals(saleType))
            return "D";
        if (codeJournalRemboursementsDecaissements.equals(journal) && "M_TVA".equals(saleType))
            return "D";
        if (codeJournalRemboursementsDecaissements.equals(journal) && "M_TTC".equals(saleType))
            return "C";
        if (codeJournalRemboursementsDecaissements.equals(journal) && "M_REM".equals(saleType))
            return "C";
        if (codeJournalRemboursementsDecaissements.equals(journal) && "M_FRA".equals(saleType))
            return "D";

        if (codeJournalFinancementEncaissements.equals(journal))
            return "C";

        if (codeJournalRemboursementClientCB_SEPA.equals(journal))
            return "D";

        if (codeJournalRemboursementClientCheque.equals(journal))
            return "D";

        if (codeJournalConstatationImpayeRejet.equals(journal))
            return "D";

        if (codeJournalRepresentationImpaye.equals(journal))
            return "C";

        return "";
    }

    /**
     * Gets the business payment method.
     *
     * @param ao the ao
     * @param journal the journal
     * @param isExludedPayment the is exluded payment
     * @return the business payment method
     */
    private String getBusinessPaymentMethod(AccountOperation ao, String journal, boolean isExludedPayment) {

        if ((journal.equals(codeJournalVentesEncaissements) || journal.equals(codeJournalRemboursementsDecaissements))
                && (ao.getMatchingStatus() != MatchingStatusEnum.L || isExludedPayment)) {
            if (PaymentMethodEnum.CARD == ao.getPaymentMethod()) {
                return "IMC";
            }
            if (PaymentMethodEnum.DIRECTDEBIT == ao.getPaymentMethod()) {
                return "IMS";
            }
        }

        if (PaymentMethodEnum.CHECK == ao.getPaymentMethod() || codeRefundCH.equals(ao.getOccCode())) {
            return "CK";
        }
        if (PaymentMethodEnum.CARD == ao.getPaymentMethod()) {

            return "CB";
        }
        if (PaymentMethodEnum.WIRETRANSFER == ao.getPaymentMethod()) {
            return "VIR";
        }
        if (PaymentMethodEnum.DIRECTDEBIT == ao.getPaymentMethod()) {

            return "PS";
        }
        return "MDT";
    }

    /**
     * Gets the code remise.
     *
     * @param ao the ao
     * @param event the event
     * @return the code remise
     */
    private String getCodeRemise(AccountOperation ao, String event) {
        if (codeEventCycleFinancementFacturation.equals(event) || codeEventGestionBanque.equals(event)) {
            if (ao.getPaymentMethod() == PaymentMethodEnum.DIRECTDEBIT) {
                if (ao.getDdRequestItem() != null && ao.getDdRequestItem().getDdRequestLOT() != null) {
                    return "" + ao.getDdRequestItem().getDdRequestLOT().getId();
                }
            }
        }

        return "";
    }

    /**
     * Generate file.
     *
     * @param lines the lines
     * @param outputDir the output dir
     * @param fromDate the from date
     * @param toDate the to date
     * @param fileName the file name
     * @throws BusinessException the business exception
     */
    public void generateFile(List<String[]> lines, String outputDir, Date fromDate, Date toDate, String fileNamePrefix) throws BusinessException {
        try {
            Provider appProvider = ((ProviderService) getServiceInterface("ProviderService")).getProvider();
            CsvBuilder csvBuilder = new CsvBuilder("", false);

            for (int i = 0; i < lines.size(); i++) {
                String[] line = lines.get(i);
                csvBuilder.appendValues(line);
                if (i != lines.size() - 1) {
                    csvBuilder.startNewLine();
                }
            }

            String absolutFileName = ParamBean.getInstance().getProperty("providers.rootDir", "/tmp/meveo/") + File.separator + appProvider.getCode();
            if (!StringUtils.isBlank(outputDir)) {
                absolutFileName += File.separator + outputDir.replaceAll("\\..", "");
            }

            absolutFileName += File.separator + fileNamePrefix + DateUtils.formatDateWithPattern(fromDate, "dd-MM-yyyy") + "_"
                    + DateUtils.formatDateWithPattern(toDate, "dd-MM-yyyy") + ".csv";

            csvBuilder.toFile(absolutFileName);
            log.info("ExportComptable created :{}", absolutFileName);
        } catch (Exception e) {
            e.printStackTrace();
            throw new BusinessException(e.getMessage());
        }

    }

    /**
     * Gets the amount as N char.
     *
     * @param value the value
     * @param nbChar the nb char
     * @return the amount as N char
     */
    public static String getAmountAsNChar(String value, int nbChar) {

        String buildString = "" + value;
        while (buildString.length() < nbChar) {
            buildString = "0" + buildString;
        }

        return buildString;
    }
