                    "srv.cf_values\\:\\:json->'CF_SE_FD_LICENCE_STATUS_HISTORY'->0->>'mapString' as history from billing_service_instance srv inner join billing_subscription sub on srv.subscription_id = sub.id   where srv.cf_values\\:\\:json->'CF_SE_FD_LICENCE_STATUS'->0->>'string' = 'RESERVED'";
            
            Map<String, Object> params = new HashMap<>();
            List<Map<String, Object>> result = subscriptionService.executeNativeSelectQuery(query, params);
            if (CollectionUtils.isNotEmpty(result)) {
                for (Map<String, Object> item : result) {
                    count++;

                    log.info("code " + item.get("code") + ", licence =" + item.get("licence") + ", history =" + getReservedDate((String)item.get("history")));

                    //log.info("KEY " + item.get("key") );
                      bufferedWriter.write(String.valueOf(item.get("licence") + "," + item.get("code") + ",") + getReservedDate((String)item.get("history")));
                      bufferedWriter.newLine();
                }
            } else {
                throw new BusinessException("No Subscription found having CF_SE_FD_LICENCE_STATUS RESERVED");
            }
        } catch (Exception e) {
                log.error(" error on findListSubscriptions  [{}]", e.getMessage());
                throw new BusinessException("error on findListSubscriptions : " + e.getMessage(), e);
            }
        log.debug(String.format(" End findListSubscriptions : [%d] items found", count));
    }

    String getReservedDate(String json) {
        JSONParser jsonParser = new JSONParser();
        JSONObject obj = null;
        String dateResult = null;
        try {
            obj = (JSONObject) jsonParser.parse(json);
            Set<String> keys = obj.keySet();
            
            for (String s : keys) {
                if ("RESERVED".equalsIgnoreCase((String)obj.get(s))) {
                    dateResult = s;
                }
            }
        } catch (ParseException e) {
            e.printStackTrace();
        }
        return dateResult;
    }
}",
