    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {


        log.info("Execute methodContext:{}", methodContext);
        Map<String, Object> lineAsMap = (Map<String, Object>) methodContext.get("record");
        String license = (String) lineAsMap.get("license");
        String subscriptionCode = (String) lineAsMap.get("subscriptionCode");
