        log.info("  license = " + license);
        log.info("  subscriptionCode = " + subscriptionCode);
        log.info("  date = " + date);

        Subscription subscription = subscriptionService.findByCode(subscriptionCode);

        String action = "ACTIVATION";
        String numeroContrat = subscriptionCode;
        String source = "OPENCELL-FNAC";
        String cle = license;
        SimpleDateFormat sdf = new SimpleDateFormat("yyMMDDhhmmsss");
        String numeroVente = sdf.format(new Date());

        Map<String, String> mapping = ((CustomFieldValue) (providerService.getProvider().getCfValues().getValuesByCode().get("CF_PROV_FD_OFFER_CODE_MAPPING")).get(0)).getMapValue();

        String offre = getOfferMapValue(mapping, subscription.getOffer().getCode());

        String engagement = "";

        if (subscription.getSubscriptionRenewal() != null) {
            if (!StringUtils.isBlank(subscription.getSubscriptionRenewal().getRenewFor())) {
                engagement = subscription.getSubscriptionRenewal().getRenewFor().toString();
            }
        }
        if ("".equals(engagement)) {
            engagement = String.valueOf(subscription.getOffer().getCfValue("CF_OF_FD_AGREEMENT"));
        }

        String recurrence = "0";
        if (SubscriptionRenewal.RenewalPeriodUnitEnum.MONTH.equals(subscription.getOffer().getSubscriptionRenewal().getRenewForUnit())) {
            recurrence = "M";
        }/* else if  (SubscriptionRenewal.RenewalPeriodUnitEnum.YEAR.equals(subscription.getOffer().getSubscriptionRenewal().getRenewForUnit())) {
            recurrence = "M";
