            throw new BusinessException("NeolaneCustomerLifeScript : cfiService is null");
        }

        ServiceInstance service = (ServiceInstance) methodContext.get("event");

        if (service != null ) {
            if (InstanceStatusEnum.ACTIVE.equals(service.getStatus())) {

                Subscription subscription = service.getSubscription();

                if (subscription == null) {
                    throw new BusinessException("NeolaneCustomerLifeScript : subscription is null");
                }

