                        throw new BusinessException("NeolaneCustomerLifeScript : serviceSearchLicence is null");
                    }
                    if ("SE_FD_LICENCE".equalsIgnoreCase(serviceSearchLicence.getCode())) {
                        offerType = "SERENITE";
