                log.info("originalDate = " + originalDate );
                String rcpId="10000" + subscription.getUserAccount().getBillingAccount().getCustomerAccount().getCustomer().getCode(); //ex : "1539786391110"
                log.info("rcpId = " + rcpId );

                String template="BIENV_OPENCELL_FPL";
                if ("SERENITE".equals(offerType)) template = "BIENV_OPENCELL_SER";
                log.info("template = " + template );

                String enseigne=subscription.getSeller().getSeller().getSeller().getCode();  // val : Fnac / Darty (Seller + 2)
                String gender = subscription.getUserAccount().getName().getTitle().getCode();
                log.info("gender = " + gender );

                String firstName=subscription.getUserAccount().getName().getFirstName();
                log.info("firstName = " + firstName );

                String lastName=subscription.getUserAccount().getName().getLastName();
                log.info("lastName = " + lastName );

                String sendBySMS="false";
                log.info("sendBySMS = " + sendBySMS );

                String sendByMail="true";
                log.info("sendByMail = " + sendByMail );

                String offerLabel=subscription.getOffer().getName();
                log.info("offerLabel = " + offerLabel );

                String contractStatus=String.valueOf(subscription.getStatus().getId());//(String) cfiService.getInheritedCFValue(service, "CF_SE_FD_LICENCE_STATUS");
                log.info("contractStatus = " + contractStatus );

                String contractNumber=subscription.getCode();
                log.info("contractNumber = " + contractNumber );

                String cardNumber = "";
                String serenityUrl= "";
                if ("SERENITE".equals(offerType)) {
                    cardNumber = licence;
                    serenityUrl = URL_SERENITE + licence;
                } else {
                    if (StringUtils.isBlank(cfiService.getInheritedCFValue(service.getSubscription(), "CF_SU_FD_CARD_NUMBER"))) {
                        cardNumber = "";
