    "code": "org.meveo.service.script.HashCalculationScript",
    "description": "Hash Calculation Script",
    "type": "JAVA",
    "script": "package org.meveo.service.script;

import org.meveo.admin.exception.BusinessException;
import org.meveo.commons.utils.EjbUtils;
import org.meveo.commons.utils.ParamBean;
import org.meveo.commons.utils.ParamBeanFactory;
import org.meveo.commons.utils.StringUtils;

import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.text.SimpleDateFormat;
import java.util.Date;
import java.util.Map;
import java.util.logging.Logger;

/**
 *
 * @author Mounir Bahije
 *
 *         Script executed to calculate md5sum of salt + date + comsign + transactionId
 *
 */
public class HashCalculationScript extends Script {

    private final static Logger logger = Logger.getLogger(HashCalculationScript.class.getName());


    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {

        ParamBeanFactory paramBeanFactory = (ParamBeanFactory) EjbUtils.getServiceInterface(ParamBeanFactory.class.getSimpleName());
        ParamBean paramBean = paramBeanFactory.getInstance();
        //Init properties
        String salt = paramBean.getProperty("salt", "$$$");

        String pattern = "yyyyMMdd";
        SimpleDateFormat simpleDateFormat = new SimpleDateFormat(pattern);
        String dateToday = simpleDateFormat.format(new Date());
        String date = (StringUtils.isBlank(methodContext.get("date"))) ? dateToday : (String) methodContext.get("date");
        String MESSAGE_TEXT = "";
        if ("$$$".equals(methodContext.get("salt"))) {
            MESSAGE_TEXT = "'salt' had to be set in properties file";
        } else {
            if ((StringUtils.isBlank(methodContext.get("comsign"))) || (StringUtils.isBlank(methodContext.get("transactionId")))) {
                MESSAGE_TEXT = "The following parameters are required : ";

                if (StringUtils.isBlank(methodContext.get("comsign"))) {
                    MESSAGE_TEXT = MESSAGE_TEXT + " " + "comsign";
                }
                if (StringUtils.isBlank(methodContext.get("transactionId"))) {
                    MESSAGE_TEXT = MESSAGE_TEXT + " " + "transactionId";
                }
            }
        }

        if (StringUtils.isBlank(MESSAGE_TEXT)) {
            String comsign = (String) methodContext.get("comsign");
            String transactionId = (String) methodContext.get("transactionId");
            String password = salt + date + comsign + transactionId;

            final byte[] defaultBytes = password.getBytes();
            try {
                final MessageDigest md5MsgDigest = MessageDigest.getInstance("MD5");
                md5MsgDigest.reset();
                md5MsgDigest.update(defaultBytes);
                final byte messageDigest[] = md5MsgDigest.digest();
                final StringBuffer hexString = new StringBuffer();
                for (final byte element : messageDigest) {
                    final String hex = Integer.toHexString(0xFF & element);
                    if (hex.length() == 1) {
                        hexString.append('0');
                    }
                    hexString.append(hex);
                }
                password = hexString + "";
            } catch (final NoSuchAlgorithmException nsae) {
                log.error("In HashCalculationScript : " + nsae.getMessage());
            }
            HashCalculationResponse response = new HashCalculationResponse(password);
            methodContext.clear();
            methodContext.put("status", "OK");
            methodContext.put("response", response);

        } else {
            HashCalculationResponse response = new HashCalculationResponse("");
            methodContext.clear();
            methodContext.put("status", "KO");
            methodContext.put("message", MESSAGE_TEXT);
        }
    }

    class HashCalculationResponse  implements java.io.Serializable {
        private String token;
        public String getToken() {
            return token;
        }
        public void setToken(String token) {
            this.token = token;
        }
        public HashCalculationResponse(String token) {
            this.token = token;
        }
        public HashCalculationResponse() {
        }
    }
}",
