 *          + source
 *          + cle
 *          + numeroVente
 */
public class ReservKeyScript extends Script {

    private static final Logger log = LoggerFactory.getLogger(ReservKeyScript.class);

    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {
        log.debug("EXECUTE  methodContext {}", methodContext);

        String type = (methodContext.get("type") == null)?  "" : (String)methodContext.get("type");
        String numeroContrat = (methodContext.get("numeroContrat") == null)?  "" : (String)methodContext.get("numeroContrat");
        String source = (methodContext.get("source") == null)?  "" : (String)methodContext.get("source");
        String cle = (methodContext.get("cle") == null)?  "" : (String)methodContext.get("cle");
        String numeroVente = (methodContext.get("numeroVente") == null)?  "" : (String)methodContext.get("numeroVente");

        String soapEnvBody = "<type xsi:type=\"xsd:string\">"+ type +"</type><numeroContrat xsi:type=\"xsd:string\">"+ numeroContrat +"</numeroContrat><source xsi:type=\"xsd:string\">"+ source +"</source><cle xsi:type=\"xsd:string\">"+ cle +"</cle><numeroVente xsi:type=\"xsd:string\">"+ numeroVente +"</numeroVente>";
        String urlWS = "http://recette.dartyserenite.com/webservices/gateway";
        String actionURI="http://recette.dartyserenite.com/webservices/gateway#reservKey";
        GatewayKeyResponse response = callWebService(soapEnvBody, urlWS, actionURI);
        methodContext.clear();
        if ( response != null ) {
            methodContext.put("statut", response.getStatut());
            methodContext.put("response", response);
        } else {
            methodContext.put("statut", "KO");
        }
    }

    public GatewayKeyResponse callWebService(String soapEnvBody, String urlWS, String actionURI) {

        // Create a StringEntity for the SOAP XML.
        String body ="<?xml version=\"1.0\" encoding=\"UTF-8\"?><soapenv:Envelope xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" xmlns:xsd=\"http://www.w3.org/2001/XMLSchema\" xmlns:soapenv=\"http://schemas.xmlsoap.org/soap/envelope/\" xmlns:gat=\""+ urlWS +"\"><soapenv:Header/><soapenv:Body><gat:reservKey soapenv:encodingStyle=\"http://schemas.xmlsoap.org/soap/encoding/\"><reservKeyRequest xsi:type=\"gat:reservKeyRequest\">"+soapEnvBody+"</reservKeyRequest></gat:reservKey></soapenv:Body></soapenv:Envelope>";
        StringEntity stringEntity = new StringEntity(body, "UTF-8");
        stringEntity.setChunked(true);
        // Request parameters and other properties.
        HttpPost httpPost = new HttpPost(urlWS);
        httpPost.setEntity(stringEntity);
        httpPost.setHeader( "SOAPAction", actionURI);

        // Execute and get the response.
        HttpClient httpClient = new DefaultHttpClient();
        HttpResponse response = null;
        try {
            response = httpClient.execute(httpPost);
            if (response != null) {

                if (response.getStatusLine().getStatusCode() >= 400) {
                    log.error("In ReservKeyScript : Status Code of response is " + response.getStatusLine().getStatusCode());
                    return null;
                }
                HttpEntity entity = response.getEntity();
                String soapResponse = EntityUtils.toString(entity);
                DocumentBuilder db = DocumentBuilderFactory.newInstance().newDocumentBuilder();
                InputSource is = new InputSource();
                is.setCharacterStream(new StringReader(soapResponse));
                Document doc = db.parse(is);
                if (doc == null) {
                    log.error("In ReservKeyScript : doc is null");
                    return null;
                }
                String cle = getCharacterDataFromElement((Element)(doc.getElementsByTagName("cle")).item(0));
                String statut = getCharacterDataFromElement((Element)(doc.getElementsByTagName("statut")).item(0));
                String type = getCharacterDataFromElement((Element)(doc.getElementsByTagName("type")).item(0));
                String motif = getCharacterDataFromElement((Element)(doc.getElementsByTagName("motif")).item(0));
                GatewayKeyResponse gatewayKeyResponse = new GatewayKeyResponse();
                gatewayKeyResponse.setCle(cle);
                gatewayKeyResponse.setStatut(statut);
                gatewayKeyResponse.setType(type);
                gatewayKeyResponse.setMotif(motif);
                return gatewayKeyResponse;
            }
        } catch (IOException e) {
            log.error("Exception in ReservKeyScript :", e);
        } catch (ParserConfigurationException e) {
            log.error("Exception in ReservKeyScript :", e);
        } catch (SAXException e) {
            log.error("Exception in ReservKeyScript :", e);
        }
        return null;
    }

    /**
     * get Data From a Dom Element
     *
     * @param element
     * @return
     */

    public static String getCharacterDataFromElement(Element element) {
        if (element == null) {
            return null;
        } else {
            Node child = element.getFirstChild();
            if (child instanceof CharacterData) {
                CharacterData cd = (CharacterData) child;
                return cd.getData();
            }
            return "";
        }
    }

    /**
     * GatewayKeyResponse : the response class of ReservKey method
     *
     */
