        StringEntity stringEntity = new StringEntity(body, "UTF-8");

        // Request parameters and other properties.
        HttpPost httpPost = new HttpPost(urlWS);
        httpPost.setEntity(stringEntity);
        httpPost.setHeader( "SOAPAction", actionURI);

        // Execute and get the response.
        HttpClient httpClient = new DefaultHttpClient();
        HttpResponse response = null;
        try {
            response = httpClient.execute(httpPost);
            if (response != null) {
                if (response.getStatusLine().getStatusCode() >= 400) {
                    log.error("In ActivateKeyScript : Status Code of response is " + response.getStatusLine().getStatusCode());
                    return null;
                }
                
                HttpEntity entity = response.getEntity();
                String soapResponse = EntityUtils.toString(entity);
                DocumentBuilder db = DocumentBuilderFactory.newInstance().newDocumentBuilder();
                InputSource is = new InputSource();
                is.setCharacterStream(new StringReader(soapResponse));
                Document doc = db.parse(is);
                if (doc == null) {
                    log.debug("doc is null");
                    return null;
                }
                if (doc.getElementsByTagName("pstrMsgId") == null) {
                    log.debug("doc.getElementsByTagName(pstrMsgId) is null");
                    return null;
                }
                
                String pstrMsgId = getCharacterDataFromElement((Element)(doc.getElementsByTagName("pstrMsgId")).item(0));
                if (pstrMsgId == null) {
                    return null;
                }

                EndSubscriptionResponse gatewayKeyResponse = new EndSubscriptionResponse();
                gatewayKeyResponse.setPstrMsgId(pstrMsgId);
                return gatewayKeyResponse;
            }
        } catch (IOException e) {
            log.error("Exception in EndSubscriptionScript :", e);
        } catch (ParserConfigurationException e) {
            log.error("Exception in EndSubscriptionScript :", e);
        } catch (SAXException e) {
            log.error("Exception in EndSubscriptionScript :", e);
        }
        return null;
    }
    
    public static String getCharacterDataFromElement(Element e) {
        if (e == null) {
            return null;
        } else {
            Node child = e.getFirstChild();
            if (child instanceof CharacterData) {
                CharacterData cd = (CharacterData) child;
                return cd.getData();
            }
            return "";
        }

    }
    
