/**
 * @author Mounir Bahije
 * <p>
 * Call ActivateKeyScript of Serenite Web Service
 * <p>
 * Execute Context Variable :
 * + action        + numeroContrat     + source        + cle
 * + numeroVente   + offre             + engagement    + recurrence
 * + essai         + login             + civilite      + nom
 * + prenom        + numVoie           + typeVoie      + nomVoie
 * + codePostal    + ville             + numTelFixe    + numTelMobile
 * + emailContact
 */


public class ActivateKeyScript extends Script {

    private SubscriptionService subscriptionService = (SubscriptionService) getServiceInterface("SubscriptionService");
    private ServiceInstanceService serviceInstanceService = (ServiceInstanceService) getServiceInterface("ServiceInstanceService");
    private CustomFieldInstanceService cfiService = (CustomFieldInstanceService) getServiceInterface("CustomFieldInstanceService");


    private int counter = 0;
    private GatewayKeyResponse gatewayKeyResponseGlobal = null;

    private ScheduledExecutorService service;


    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {
        log.debug("EXECUTE  methodContext {}", methodContext);

        String action = (methodContext.get("action") == null) ? "" : (String) methodContext.get("action");
        String numeroContrat = (methodContext.get("numeroContrat") == null) ? "" : (String) methodContext.get("numeroContrat");
        String source = (methodContext.get("source") == null) ? "" : (String) methodContext.get("source");
        String cle = (methodContext.get("cle") == null) ? "" : (String) methodContext.get("cle");
        String numeroVente = (methodContext.get("numeroVente") == null) ? "" : (String) methodContext.get("numeroVente");
        String offre = (methodContext.get("offre") == null) ? "" : (String) methodContext.get("offre");
        String engagement = (methodContext.get("engagement") == null) ? "" : (String) methodContext.get("engagement");
        String recurrence = (methodContext.get("recurrence") == null) ? "" : (String) methodContext.get("recurrence");
        String essai = (methodContext.get("essai") == null) ? "" : (String) methodContext.get("essai");
        String login = (methodContext.get("login") == null) ? "" : (String) methodContext.get("login");
        String civilite = (methodContext.get("civilite") == null) ? "" : (String) methodContext.get("civilite");
        String nom = (methodContext.get("nom") == null) ? "" : (String) methodContext.get("nom");
        String prenom = (methodContext.get("prenom") == null) ? "" : (String) methodContext.get("prenom");
        String numVoie = (methodContext.get("numVoie") == null) ? "" : (String) methodContext.get("numVoie");
