        String codePostal = (methodContext.get("codePostal") == null) ? "" : (String) methodContext.get("codePostal");
        String ville = (methodContext.get("ville") == null) ? "" : (String) methodContext.get("ville");
        String numTelFixe = (methodContext.get("numTelFixe") == null) ? "" : (String) methodContext.get("numTelFixe");
        String numTelMobile = (methodContext.get("numTelMobile") == null) ? "" : (String) methodContext.get("numTelMobile");
        String emailContact = (methodContext.get("emailContact") == null) ? "" : (String) methodContext.get("emailContact");

        String soapEnvBody = "" +
                "<action>" + action + "</action>" +
                "<numeroContrat>" + numeroContrat + "</numeroContrat>" +
                "<source>" + source + "</source>" +
                "<cle>" + cle + "</cle>" +
                "<numeroVente>" + numeroVente + "</numeroVente>" +
                "<offre>" + offre + "</offre>" +
                "<engagement>" + engagement + "</engagement>" +
