                "<numTelFixe>" + numTelFixe + "</numTelFixe>" +
                "<numTelMobile>" + numTelMobile + "</numTelMobile>" +
                "<emailContact>" + emailContact + "</emailContact>" +
                "";


        String urlWS = "http://recette.dartyserenite.com/webservices/gateway";
        String actionURI="http://recette.dartyserenite.com/webservices/gateway#activateKey";

        GatewayKeyResponse response = callWebService(soapEnvBody, urlWS, actionURI, numeroContrat);
        methodContext.clear();
        if (response != null) {
            methodContext.put("statut", response.getStatut());
            methodContext.put("response", response);
        } else {
            methodContext.put("statut", "KO");
