    public GatewayKeyResponse callWebService(String soapEnvBody, String urlWS, String actionURI, String numeroContrat) {

        String body = "<soapenv:Envelope xmlns:soapenv=\"http://schemas.xmlsoap.org/soap/envelope/\" xmlns:gat=\"" + urlWS + "\"><soapenv:Header/><soapenv:Body><gat:activateKey><activateKeyRequest>" + soapEnvBody + "</activateKeyRequest></gat:activateKey></soapenv:Body></soapenv:Envelope>";

        StringEntity stringEntity = new StringEntity(body, "UTF-8");
        stringEntity.setChunked(true);
        // Request parameters and other properties.
        HttpPost httpPost = new HttpPost(urlWS);
        httpPost.setEntity(stringEntity);
        httpPost.setHeader("SOAPAction", actionURI);

        // Execute and get the response.
        HttpClient httpClient = new DefaultHttpClient();
        HttpResponse response = null;
        int x_attempts = 0;
        int y_delay = 0;
        ParamBeanFactory paramBeanFactory = (ParamBeanFactory) EjbUtils.getServiceInterface(ParamBeanFactory.class.getSimpleName());

        ParamBean paramBean = paramBeanFactory.getInstance();
        String xStrAttempts = paramBean.getProperty("activateKey.x.attempts", "0");
        String yStrDelay = paramBean.getProperty("activateKey.y.delay", "0");
        x_attempts = Integer.valueOf(xStrAttempts);
        y_delay = Integer.valueOf(yStrDelay);
        try {
            response = httpClient.execute(httpPost);
            if (response != null) {
                if (response.getStatusLine().getStatusCode() != 200) {
                    log.error("In ActivateKeyScript : Status Code of response is " + response.getStatusLine().getStatusCode());
                    try {
                        loopCallWS(x_attempts, y_delay, httpPost, numeroContrat);
                    } catch (Exception e) {
                        log.error("Exception in ActivateKeyScript.retryCallWebService :", e);
                    }
                }
                return gatewayKeyResponseFromHttpResponse(response);
            }
        } catch (Exception e) {
            loopCallWS(x_attempts, y_delay, httpPost, numeroContrat);
            log.error("Exception in ActivateKeyScript :", e);
        }
        return null;
    }

    public GatewayKeyResponse gatewayKeyResponseFromHttpResponse(HttpResponse response) throws IOException, ParserConfigurationException, SAXException {
        HttpEntity entity = response.getEntity();
        String soapResponse = EntityUtils.toString(entity);
        DocumentBuilder db = DocumentBuilderFactory.newInstance().newDocumentBuilder();
        InputSource is = new InputSource();
        is.setCharacterStream(new StringReader(soapResponse));
        Document doc = db.parse(is);
        if (doc == null) {
            log.error("Error in ActivateKeyScript : doc is null");
            return null;
        }
        String cle = getCharacterDataFromElement((Element) (doc.getElementsByTagName("cle")).item(0));
        String statut = getCharacterDataFromElement((Element) (doc.getElementsByTagName("statut")).item(0));
        String type = getCharacterDataFromElement((Element) (doc.getElementsByTagName("type")).item(0));
        String motif = getCharacterDataFromElement((Element) (doc.getElementsByTagName("motif")).item(0));
        GatewayKeyResponse gatewayKeyResponse = new GatewayKeyResponse();
        gatewayKeyResponse.setCle(cle);
        gatewayKeyResponse.setStatut(statut);
        gatewayKeyResponse.setType(type);
        gatewayKeyResponse.setMotif(motif);
        return gatewayKeyResponse;
    }

    public void loopCallWS(int x_attempts, int y_delay, HttpPost httpPost, String numeroContrat) {
        Runnable runnable = new Runnable() {
            public void run() {
                retryCallWebService(httpPost, x_attempts, numeroContrat);
            }
        };
        service = Executors.newSingleThreadScheduledExecutor();
        service.scheduleAtFixedRate(runnable, 0, y_delay, TimeUnit.SECONDS);
    }

    public void retryCallWebService(HttpPost httpPost, int x_attempts, String numeroContrat) {
        counter++;
        try {
            CloseableHttpAsyncClient client = HttpAsyncClients.createDefault();
            client.start();
            Future<HttpResponse> future = client.execute(httpPost, null);
            HttpResponse response = null;

            if (counter > x_attempts) {
                future.cancel(false);
                service.shutdown();
            }
            response = future.get();

            if (response != null) {
                if (response.getStatusLine().getStatusCode() == 200) {
                    log.info("in retryCallWebService OK : " + response.getStatusLine());
                    if (subscriptionService == null) {
                        log.error("Failed to load subscriptionService");
                        throw new RuntimeException("Failed to load subscriptionService");
                    }
                    Subscription subscription =  subscriptionService.findByCode(numeroContrat);

                    if (subscription == null) {
                        log.error("Failed to find subscription by code : " + numeroContrat);
                        throw new ElementNotFoundException(numeroContrat, "subscription" );
                    }
                    SimpleDateFormat df = new SimpleDateFormat("DD/MM/YY hh:mm:ss");
                    String  currentDate = df.format(new Date());
                    for (ServiceInstance service : subscription.getServiceInstances()) {
                        if ("SE_FD_LICENCE".equals(service.getCode())) {
                            boolean isActivable = true;
                            try {
                                log.debug("serviceInstance={}", service);
                                cfiService.setCFValue(service, "CF_SE_FD_LICENCE_STATUS", "ACTIVABLE");
                                log.info("Setting CF_SE_FD_LICENCE_STATUS to ACTIVABLE");

                                Map<String, Object> statusHistory = (Map<String, Object>) cfiService.getCFValue(service, "CF_SE_FD_LICENCE_STATUS_HISTORY");
                                if (statusHistory == null) {
                                    statusHistory = new HashMap<String, Object>();
                                }
                                statusHistory.put(currentDate, "ACTIVABLE");
                                cfiService.setCFValue(service, "CF_SE_FD_LICENCE_STATUS_HISTORY", statusHistory);

                            } catch (BusinessException e) {
                                log.error("Failed to activate service : " + e);
                            }
                        }
                    }
                    future.cancel(false);
                    service.shutdown();
                }
            }
        } catch (InterruptedException e) {
            log.error("Exception in ActivateKeyScript :", e);
        } catch (ExecutionException e) {
            log.error("Exception in ActivateKeyScript :", e);
        } catch (ElementNotFoundException e) {
            log.error("Exception in ActivateKeyScript :", e);
        }
    }

    /**
     * get Data From a Dom Element
     *
     * @param element
     * @return
     */

    public static String getCharacterDataFromElement(Element element) {
        Node child = element.getFirstChild();
        if (child instanceof CharacterData) {
            CharacterData cd = (CharacterData) child;
            return cd.getData();
        }
        return "";
    }

    /**
