    private static final String CF_LICENCE_STATUS = "CF_SE_{{template.code}}_LICENCE_STATUS";
    private static final String SE_LICENCE = "SE_{{template.code}}_LICENCE";
    private final CustomFieldInstanceService customFieldInstanceService = (CustomFieldInstanceService) getServiceInterface(CustomFieldInstanceService.class.getSimpleName());
    private final static String FILE_PREFIX = "RESILIATION_DARTY_SERENITE_NOTIF_";
    private final static String SERENITY_STATUS_DF = "dd/MM/yy HH:mm:ss";
    private final static String SERENITY_NOTIF_DF = "yyyy-MM-dd'T'HH:mm:ss";

    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {
        List<Subscription> listSereniteSubscriptions = this.findDailyRevokedSereniteSubscriptions();
        if (isEmpty(listSereniteSubscriptions)) {
            throw new BusinessException(" No resilied Serenite subscription found today !");
        }

        String filePath = getFilePath(methodContext);
        log.debug(" generating Serenity Notifications File in [{}] ", filePath);

        this.generateSereniteNotificationsFile(listSereniteSubscriptions, filePath);
    }

    private String getFilePath(Map<String, Object> methodContext) {
        String outputDir = (String) methodContext.get("OUTPUT_DIR");
        outputDir = StringUtils.defaultIfEmpty(outputDir, "/subscriptions/serenity"); // TODO

        final ParamBeanFactory paramBeanFactory = (ParamBeanFactory) getServiceInterface(ParamBeanFactory.class.getSimpleName());
        outputDir = paramBeanFactory.getChrootDir() + File.separator + outputDir;

        File dir = new File(outputDir);
        if (!dir.exists()) {
            dir.mkdirs();
        }
        String filePath = outputDir + File.separator + FILE_PREFIX + DateUtils.formatDateWithPattern(new Date(), "yyyyMMddHHmmss") + ".xml";
        return filePath;
    }

    private void generateSereniteNotificationsFile(List<Subscription> subscriptions, String filePath) throws BusinessException {

        log.debug(" >>> Start  generateSereniteNotificationsFile filePath : {} ", filePath);

        try (FileWriter fileWriter = new FileWriter(filePath);) {

            String dateDemande = DateUtils.formatDateWithPattern(new Date(), SERENITY_NOTIF_DF);
            Document document = DocumentHelper.createDocument();
            Element root = document.addElement("not:notificationsSerenite").addNamespace("not", "http://www.dartybox.com/serenite/notifications");

            for (Subscription s : subscriptions) {

                Element item = root.addElement("not:notificationSerenite");
                item.addElement("not:numeroContrat").addText(s.getCode());
                item.addElement("not:typeActe").addText("RESILIATION");
                item.addElement("not:dateDemande").addText(dateDemande);
                item.addElement("not:dateEffetResiliation").addText(DateUtils.formatDateWithPattern(s.getStatusDate(), SERENITY_NOTIF_DF));
                SubscriptionTerminationReason reason = s.getSubscriptionTerminationReason();
                item.addElement("not:libelleMotifResiliation").addText(reason != null ? reason.getDescriptionAndCode() : "");

                this.addLicenceStatusHistory(s, "TERMINATION_PENDING"); // Ask a question about generating the file in case of failure
            }

            XMLWriter writer = new XMLWriter(fileWriter, OutputFormat.createPrettyPrint());
            writer.write(document);

            log.debug(" >>> Successfully generated file : {} ", filePath);
        } catch (Exception e) {
            log.error(" Error generateSereniteNotificationsFile {} ", e.getMessage(), e);
            this.deleteIfExists(filePath);
            throw new BusinessException(" Error on generateSereniteNotificationsFile", e);
        }
    }

    private void deleteIfExists(String filePath) {
        try {
            Files.deleteIfExists(Paths.get(filePath));
        } catch (IOException e) {
            log.error(" Cannot deleting file {} ", filePath, e);
        }
    }

    @SuppressWarnings("unchecked")
    private void addLicenceStatusHistory(Subscription s, String status) throws BusinessException {

        // getting the Serenity ServiceInstance :
        ServiceInstance serenety = getSerenityServiceInstance(s);
        if (serenety == null) {
            throw new BusinessException(" No Serenity service found for Subscription code = " + s.getCode());
        }

        // adding a senerity
        Map<String, String> licenceStatusHistory = (Map<String, String>) customFieldInstanceService.getCFValue(serenety, CF_LICENCE_STATUS_HISTORY); 
        if (MapUtils.isEmpty(licenceStatusHistory)) {
            licenceStatusHistory = Maps.newLinkedHashMap();
        }

        log.debug(" changing Licence status to : {} ", status);
        this.customFieldInstanceService.setCFValue(serenety, CF_LICENCE_STATUS, status);

        licenceStatusHistory.put(DateUtils.formatDateWithPattern(new Date(), SERENITY_STATUS_DF), status);
        log.debug(" licenceStatusHistory : {} ", licenceStatusHistory);
        this.customFieldInstanceService.setCFValue(serenety, CF_LICENCE_STATUS_HISTORY, licenceStatusHistory);
       
    }

    @SuppressWarnings("unchecked")
    private ServiceInstance getSerenityServiceInstance(Subscription s) {
        log.debug(" Start getSerenityServiceInstance for Subscription [id = {} ] ", s.getId());
        try {
            String query = String.format("select distinct si  from ServiceInstance si  where si.subscription =:subscription and si.code = '%s' ", SE_LICENCE);

            Map<String, Object> params = new HashMap<>();
            params.put("subscription", s);
            final ServiceInstanceService serviceInstanceService = (ServiceInstanceService) getServiceInterface(ServiceInstanceService.class.getSimpleName());
            List<ServiceInstance> result = (List<ServiceInstance>) serviceInstanceService.executeSelectQuery(query.toString(), params);

            log.debug(" getSerenityServiceInstance result [ {} ] ", result);
            return CollectionUtils.isNotEmpty(result) ? result.get(0) : null;

        } catch (Exception e) {
            log.error(" Error getSerenityServiceInstance {} ", e.getMessage(), e);
            return null;
        }
    }

    @SuppressWarnings("unchecked")
    private List<Subscription> findDailyRevokedSereniteSubscriptions() {

        log.debug(" Start findDailyRevokedSereniteServices ... ");
        try {
            StringBuffer query = new StringBuffer("select distinct s from Subscription s JOIN s.serviceInstances si where si.code = '").append(SE_LICENCE)
                .append("' and s.status = 'RESILIATED' ").append(" and  function('to_char', function('date_trunc', 'D', s.statusDate) , 'dd/MM/yyyy')  =:today ");

            Map<String, Object> params = new HashMap<>();
            String today = DateUtils.formatDateWithPattern(new Date(), "dd/MM/yyyy");
            params.put("today", today);
            final SubscriptionService subscriptionService = (SubscriptionService) getServiceInterface(SubscriptionService.class.getSimpleName());
            return (List<Subscription>) subscriptionService.executeSelectQuery(query.toString(), params);

        } catch (Exception e) {
            log.error(" Error findDailyRevokedSereniteServices {} ", e.getMessage(), e);
            return null;
        }
    }
}",
