    "sourcingRoles": []
}

{
    "code": "org.meveo.service.script.ImportSerenityResiliationScript",
    "description": "ImportSerenityResiliationScript",
    "type": "JAVA",
    "script": "package org.meveo.service.script;

import java.io.File;
import java.util.ArrayList;
import java.util.Collection;
import java.util.Collections;
import java.util.Date;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

import org.apache.commons.collections4.CollectionUtils;
import org.apache.commons.collections4.MapUtils;
import org.apache.commons.lang3.StringUtils;
import org.dom4j.Document;
import org.dom4j.DocumentException;
import org.dom4j.Node;
import org.dom4j.io.SAXReader;
import org.meveo.admin.exception.BusinessException;
import org.meveo.commons.utils.EjbUtils;
import org.meveo.commons.utils.FileUtils;
import org.meveo.commons.utils.ParamBeanFactory;
import org.meveo.model.billing.ServiceInstance;
import org.meveo.model.shared.DateUtils;
import org.meveo.service.billing.impl.ServiceInstanceService;
import org.meveo.service.crm.impl.CustomFieldInstanceService;

import com.google.common.collect.Maps;

public class ImportSerenityResiliationScript extends Script {

    private static final String CF_LICENCE_STATUS_HISTORY = "CF_SE_{{template.code}}_LICENCE_STATUS_HISTORY";
    private static final String CF_LICENCE_STATUS = "CF_SE_{{template.code}}_LICENCE_STATUS";  
    private static final String SE_LICENCE = "SE_{{template.code}}_LICENCE";
    private final CustomFieldInstanceService customFieldInstanceService = (CustomFieldInstanceService) getServiceInterface(CustomFieldInstanceService.class.getSimpleName());
    private final ParamBeanFactory paramBeanFactory = (ParamBeanFactory) getServiceInterface(ParamBeanFactory.class.getSimpleName());

    private final static String FILE_PREFIX = "RESILIATION_DARTY_SERENITE_CR_";
    private final static String SERENITY_STATUS_DF = "dd/MM/yy HH:mm:ss";
    private final static String SERENITY_NOTIF_DF = "yyyy-MM-dd'T'HH:mm:ss";

    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {

        String inputDir = (String) methodContext.get("INPUT_DIR");

        try {

            // Getting inputDir
            inputDir = StringUtils.defaultIfEmpty(inputDir, "/subscriptions/serenity/improt");
            inputDir = paramBeanFactory.getChrootDir() + inputDir;

            String outputDir = inputDir + File.separator + "output";
            String rejectDir = inputDir + File.separator + "reject";
            String archiveDir = inputDir + File.separator + "archive";

            this.preparDirectories(outputDir, rejectDir, archiveDir, inputDir);

            ArrayList<String> fileExtensions = new ArrayList<String>();
            fileExtensions.add("xml");

            File[] files = FileUtils.getFilesForParsing(inputDir, fileExtensions, FILE_PREFIX);

            if (files == null || files.length == 0) {
                throw new BusinessException(" No Serenity Resiliation CR files found !");
            } else {
                int numberOfFiles = files.length;
                log.info("InputFiles job " + numberOfFiles + " to import");

                for (File file : files) {
                    File currentFile = null;
                    String fileName = null;
                    try {
                        fileName = file.getName();
                        log.info(fileName + " in progress");
                        currentFile = FileUtils.addExtension(file, ".processing_" + EjbUtils.getCurrentClusterNode());

                        this.processSerenityResiliationFile(currentFile);

                        FileUtils.moveFile(archiveDir, currentFile, fileName);
                        log.info("Processing " + file.getName() + " done");
                    } catch (Exception e) {
                        log.error("Processing " + file.getName() + " failed", e);
                        FileUtils.moveFile(rejectDir, currentFile, fileName);
                    } finally {
                        if (currentFile != null) {
                            currentFile.delete();
                        }
                    }
                }
            }

        } catch (BusinessException e) {
            throw e;
        } catch (Exception e) {
            throw new BusinessException(" Error processing Serenity ResiliationScript CR files in : " + inputDir, e);
        }
    }

    private void preparDirectories(String outputDir, String rejectDir, String archiveDir, String inputDir) {
        File f = new File(outputDir);
        if (!f.exists()) {
            log.debug("outputDir {} not exist", outputDir);
            f.mkdirs();
            log.debug("outputDir {} creation ok", outputDir);
        }
        f = new File(rejectDir);
        if (!f.exists()) {
            log.debug("rejectDir {} not exist", rejectDir);
            f.mkdirs();
            log.debug("rejectDir {} creation ok", rejectDir);
        }
        f = new File(archiveDir);
        if (!f.exists()) {
            log.debug("saveDir {} not exist", archiveDir);
            f.mkdirs();
            log.debug("saveDir {} creation ok", archiveDir);
        }
        // Getting files from inputDir :
        log.info("inputDir=" + inputDir);
        f = new File(inputDir);
        if (!f.exists()) {
            f.mkdirs();
        }
    }

    @SuppressWarnings("unchecked")
    private void processSerenityResiliationFile(File currentFile) throws DocumentException, BusinessException {
        SAXReader saxReader = new SAXReader();
        Document document = saxReader.read(currentFile);
        List<Node> elements = document.selectNodes("/not:resiliationsSerenite/not:resiliationSerenite");

        for (Node node : safe(elements)) {
            String numeroContrat = null;
            String dateToPutInHistory  = null;
            try {
                numeroContrat = node.selectSingleNode("not:numeroContrat").getText();
                log.debug(" >>> numeroContrat : {} " , numeroContrat);
                String datePriseEnCompte = node.selectSingleNode("not:datePriseEnCompte").getText();
                log.debug(" >>> datePriseEnCompte : {} " , datePriseEnCompte);

                ServiceInstance serenityService = this.getSerenityServiceInstance(numeroContrat);
                
                if (serenityService != null) {
                    dateToPutInHistory = changeFormat(datePriseEnCompte , SERENITY_NOTIF_DF , SERENITY_STATUS_DF); 
                    if (StringUtils.isEmpty(dateToPutInHistory)) {
                        dateToPutInHistory = datePriseEnCompte; // TODO  ask for confirmation of date pattern.
                    }
                    
                    log.debug(" Start addLicenceStatusHistory [numeroContrat = {} , dateToPutInHistory = {}]", numeroContrat, dateToPutInHistory);
                    this.addLicenceStatusHistory(serenityService, "TERMINATED", dateToPutInHistory);
                } else {
                    log.error(" No Serennity service found for Subscription having code = {}", numeroContrat);
                }
            } catch (Exception e) {
                log.error(" Error addLicenceStatusHistory [numeroContrat = {} , dateToPutInHistory = {}]", numeroContrat, dateToPutInHistory);
            }
        }
    }
    
    @SuppressWarnings("unchecked")
    private void addLicenceStatusHistory(ServiceInstance serenety, String status, String datePriseEnCompte) throws BusinessException {
        // adding a senerity
        Map<String, String> licenceStatusHistory = (Map<String, String>) customFieldInstanceService.getCFValue(serenety, CF_LICENCE_STATUS_HISTORY); 
        if (MapUtils.isEmpty(licenceStatusHistory)) {
            licenceStatusHistory = Maps.newLinkedHashMap();
        }

        log.debug(" changing Licence status to : {} ", status);
        this.customFieldInstanceService.setCFValue(serenety, CF_LICENCE_STATUS, status);

        licenceStatusHistory.put(datePriseEnCompte, status);

        log.debug(" licenceStatusHistory : {} ", licenceStatusHistory);
        this.customFieldInstanceService.setCFValue(serenety, CF_LICENCE_STATUS_HISTORY, licenceStatusHistory);  
    }

    @SuppressWarnings("unchecked")
    private ServiceInstance getSerenityServiceInstance(String subscriptionCode) {
        log.debug(" Start getSerenityServiceInstance for subscriptionCode [id = {} ] ", subscriptionCode);
        try {
            String query = String.format("select distinct si  from ServiceInstance si  where si.subscription.code =:subscriptionCode and si.code = '%s' ", SE_LICENCE);

            Map<String, Object> params = new HashMap<>();
            params.put("subscriptionCode", subscriptionCode);
            final ServiceInstanceService serviceInstanceService = (ServiceInstanceService) getServiceInterface(ServiceInstanceService.class.getSimpleName());
            List<ServiceInstance> result = (List<ServiceInstance>) serviceInstanceService.executeSelectQuery(query.toString(), params);

            log.debug(" getSerenityServiceInstance result [ {} ] ", result);
            return CollectionUtils.isNotEmpty(result) ? result.get(0) : null;

        } catch (Exception e) {
            log.error(" Error getSerenityServiceInstance {} ", e.getMessage(), e);
            return null;
        }
    }

    public String changeFormat(String dateValue, String fromFormat, String toFormat) {
        try {
            if (StringUtils.isEmpty(dateValue) || StringUtils.isEmpty(fromFormat) || StringUtils.isEmpty(toFormat)) {
                return dateValue;
            }
            
            Date date = DateUtils.parseDateWithPattern(dateValue, fromFormat);
            return DateUtils.formatDateWithPattern(date, toFormat);
            
        } catch (Exception e) {
            log.error(" error on changeFormat : [{}] ", e.getMessage());
            return dateValue;
        }
    }
  

    @SuppressWarnings("unchecked")
    private <T> Collection<T> safe(Collection<T> collection) {
        return collection == null ? Collections.EMPTY_LIST : collection;
    }
}",
    "executionRoles": [],
    "sourcingRoles": []
}

{
    "code": "org.meveo.service.script.SubsResiliationAndEndOfTermNotifScript",
    "description": "Subscription Resiliation & EndOfTermNotifScript",
    "type": "JAVA",
    "script": "package org.meveo.service.script;

import static org.meveo.model.shared.DateUtils.addDaysToDate;
import static org.meveo.model.shared.DateUtils.formatDateWithPattern;
import static org.meveo.model.shared.DateUtils.truncateTime;

import java.math.BigDecimal;
import java.util.Date;
import java.util.Map;

import javax.ws.rs.client.Entity;
import javax.ws.rs.core.Response;

import org.apache.commons.lang3.StringUtils;
import org.jboss.resteasy.client.jaxrs.ResteasyClient;
import org.jboss.resteasy.client.jaxrs.ResteasyClientBuilder;
import org.jboss.resteasy.client.jaxrs.ResteasyWebTarget;
import org.meveo.admin.exception.BusinessException;
import org.meveo.event.FieldAudit;
import org.meveo.model.ICustomFieldEntity;
import org.meveo.model.billing.Subscription;
import org.meveo.model.billing.SubscriptionStatusEnum;
import org.meveo.service.crm.impl.CustomFieldInstanceService;

import com.fasterxml.jackson.annotation.JsonInclude;
import com.fasterxml.jackson.annotation.JsonInclude.Include;

public class SubsResiliationAndEndOfTermNotifScript extends Script {

    private static final String WS_SUBSCRIPTION_URL = "WS_SUBSCRIPTION_URL";
    private static final String CF_OF_SU_FD_TRY_DURATION = "CF_OF_SU_FD_TRY_DURATION";
    private static final String NEW_END_DATE_PATTERN = "yyyy-MM-dd HH:mm:ss";

    private CustomFieldInstanceService customFieldInstanceService = (CustomFieldInstanceService) getServiceInterface(CustomFieldInstanceService.class.getSimpleName());

    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {

        try {

            final String wsUrl = (String) methodContext.get(WS_SUBSCRIPTION_URL);
            if (StringUtils.isEmpty(wsUrl)) {
                throw new BusinessException(" Missing param WS_SUBSCRIPTION_URL , WS url cannot be null ");
            }

            log.debug(" >>> methodContext {} ", methodContext.entrySet());

            Object entityOrEvent = methodContext.get("entityOrEvent");
            if (entityOrEvent == null) {
                throw new BusinessException(" Subscription entityOrEvent cannot be null ");
            }
            Subscription s = null;
            if (entityOrEvent instanceof FieldAudit) {
                s = (Subscription) ((FieldAudit) methodContext.get("entityOrEvent")).getEntity();
            } else {
                s = (Subscription) methodContext.get("entityOrEvent");
            }

            String idCustomer = s.getUserAccount().getBillingAccount().getCustomerAccount().getCustomer().getCode();
            String contractNumber = s.getCode();
            log.debug(" customId = {} , contractNumber = {} ", idCustomer, contractNumber);

            Date subscribedTillDate = truncateMandatoryDate(s.getSubscribedTillDate(), "subscribedTillDate");
            Date subscriptionDate = truncateMandatoryDate(s.getSubscriptionDate(), "subscriptionDate");
            Long tryDuration = (Long) this.fetchCFConfigValue(s, CF_OF_SU_FD_TRY_DURATION, false);
            if (tryDuration == null) {
                tryDuration = BigDecimal.ZERO.longValue();
            }
            Date subscriptionDateWithTryDuration = addDaysToDate(subscriptionDate, tryDuration.intValue());

            log.debug("subscribedTillDate = {} , subscriptionDate = {} , tryDuration = {}, subscriptionDateWithTryDuration = {} ", subscribedTillDate, subscriptionDate,
                tryDuration, subscriptionDateWithTryDuration);

            TerminationNotif notif = new TerminationNotif(contractNumber, idCustomer);
            ResteasyClient client = new ResteasyClientBuilder().build();

            String reason = null;
            ResteasyWebTarget target = null;

            // Getting the reason :
            SubscriptionStatusEnum status = s.getStatus();
            if (status == SubscriptionStatusEnum.RESILIATED) {
                // If resiliation then terminationDate is mandatory ;
                Date terminationDate = truncateMandatoryDate(s.getTerminationDate(), "terminationDate");
                reason = this.getResiliationReason(subscribedTillDate, subscriptionDate, terminationDate, subscriptionDateWithTryDuration);
                log.debug("terminationDate = {} , reason = {} ", terminationDate, reason);
                notif.setReason(reason);
                target = client.target(wsUrl.concat("/interrupt-contract"));
            } else { // End of Term
                reason = this.getUpdateReason(subscribedTillDate, subscriptionDate, subscriptionDateWithTryDuration);
                notif.setReason(reason);
                notif.setNewEndDate(formatDateWithPattern(subscribedTillDate, NEW_END_DATE_PATTERN));
                notif.setPrice("");
                log.debug(" newEndDate = {} , reason = {} ", subscribedTillDate, reason);
                target = client.target(wsUrl.concat("/update-enddate"));
            }

            Response response = target.request().post(Entity.json(notif));
            String firedUri = target.getUri().toString();

            if (Response.Status.Family.SUCCESSFUL == response.getStatusInfo().getFamily()) {
                methodContext.put(JOB_RESULT_NB_OK, "1");
                methodContext.put(JOB_RESULT_REPORT, "WS [".concat(target.getUri().toString()).concat("] fired successfully for Subscription code = ").concat(contractNumber));
            } else {
                methodContext.put(JOB_RESULT_NB_KO, "1");
                String msg = "WS [".concat(firedUri).concat("] Error for Subscription code = [").concat(contractNumber).concat("] : ").concat(response.toString());
                methodContext.put(JOB_RESULT_REPORT, msg);
                throw new BusinessException(msg);
            }

        } catch (Exception e) {
            // throw new BusinessException(" Error : " + e.getMessage());
          log.error("Error : {} ",e.getMessage(), e); // to dont rollback main transaction ...
        }
    }

    private Date truncateMandatoryDate(Date date, String dateName) throws BusinessException {
        if (date == null) {
            throw new BusinessException(dateName.concat(" cannot be null"));
        }
        return truncateTime(date);
    }

    private String getUpdateReason(Date subscribedTillDate, Date subscriptionDate, Date subscriptionDateWithTryDuration) throws BusinessException {
        Date today = truncateTime(new Date());
        if (subscribedTillDate.after(subscriptionDateWithTryDuration) && today.equals(subscriptionDateWithTryDuration)) {
            return "CONVERT";
        } else if (subscribedTillDate.after(subscriptionDateWithTryDuration) && subscriptionDateWithTryDuration.before(today)) {
            return "RENEW";
        }
        // There should be no functional case for that
        throw new BusinessException(String.format("Invalid Reason for : [subscribedTillDate=%s, subscriptionDate=%s, subscriptionDateWithTryDuration=%s]", subscribedTillDate,
            subscriptionDate, subscriptionDateWithTryDuration));
    }

    private String getResiliationReason(Date subscribedTillDate, Date subscriptionDate, Date terminationDate, Date subscriptionDateWithTryDuration) throws BusinessException {
        if (this.isNoConvert(subscribedTillDate, subscriptionDate, terminationDate, subscriptionDateWithTryDuration)) {
            return "NO_CONVERT";
        } else if (this.isNoRenew(subscribedTillDate, subscriptionDate, terminationDate, subscriptionDateWithTryDuration)) {
            return "NO_RENEW";
        }
        // There should be no functional case for that
        throw new BusinessException(String.format("Invalid Reason for : [subscribedTillDate=%s, subscriptionDate=%s, terminationDate=%s, subscriptionDateWithTryDuration=%s]",
            subscribedTillDate, subscriptionDate, terminationDate, subscriptionDateWithTryDuration));
    }

    private boolean isNoRenew(Date subscribedTillDate, Date subscriptionDate, Date terminationDate, Date subscriptionDateWithTryDuration) {
        // terminationDate == subscribedUntilDate && subscribedUntilDate > subscriptionDate+tryDuration
        // NO_RENEW ==> un client ayant déjà son contrat payant en cours à décider de ne pas le renouveler
        boolean isNoRenew = terminationDate.equals(subscribedTillDate) && subscribedTillDate.after(subscriptionDateWithTryDuration);
        log.debug(" isNoRenew = {} ", isNoRenew);
        return isNoRenew;
    }

    private boolean isNoConvert(Date subscribedTillDate, Date subscriptionDate, Date terminationDate, Date subscriptionDateWithTryDuration) {
        // terminationDate <= subscribedUntilDate && subscribedUntilDate == subscriptionDate+tryDuration
        // NO_CONVERT ==> le client n'a pas converti son essai
        boolean isNoConvert = (terminationDate.before(subscribedTillDate) || terminationDate.equals(subscribedTillDate))
                && subscribedTillDate.equals(subscriptionDateWithTryDuration);
        log.debug(" isNoConvert = {} ", isNoConvert);
        return isNoConvert;
    }

    private Object fetchCFConfigValue(ICustomFieldEntity entity, String cfKey, boolean isMandatory) throws BusinessException {
        Object cfValue = customFieldInstanceService.getCFValue(entity, cfKey);
        if (isMandatory && (cfValue == null || (cfValue instanceof String && StringUtils.isEmpty(String.valueOf(cfValue))))) {
            throw new BusinessException(" Missing mandatory config [" + cfKey + "]");
        }
        return cfValue;
    }

    @JsonInclude(Include.NON_NULL)
    class TerminationNotif {

        private String contractNumber;
        private String idCustomer;
        private String newEndDate;
        private String reason;
        private String price;

        TerminationNotif(String contractNumber, String idCustomer) {
            this.contractNumber = contractNumber;
            this.idCustomer = idCustomer;
        }

        /**
         * @return the contractNumber
         */
        public String getContractNumber() {
            return contractNumber;
        }

        /**
         * @return the idCustomer
         */
        public String getIdCustomer() {
            return idCustomer;
        }

        /**
         * @return the newEndDate
         */
        public String getNewEndDate() {
            return newEndDate;
        }

        /**
         * @return the reason
         */
        public String getReason() {
            return reason;
        }

        /**
         * @return the price
         */
        public String getPrice() {
            return price;
        }

        /**
         * @param contractNumber the contractNumber to set
         */
        public void setContractNumber(String contractNumber) {
            this.contractNumber = contractNumber;
        }

        /**
         * @param idCustomer the idCustomer to set
         */
        public void setIdCustomer(String idCustomer) {
            this.idCustomer = idCustomer;
        }

        /**
         * @param newEndDate the newEndDate to set
         */
        public void setNewEndDate(String newEndDate) {
            this.newEndDate = newEndDate;
        }

        /**
         * @param reason the reason to set
         */
        public void setReason(String reason) {
            this.reason = reason;
        }

        /**
         * @param price the price to set
         */
        public void setPrice(String price) {
            this.price = price;
        }
    }
}
",
    "executionRoles": [],
    "sourcingRoles": []
}

{
    "code": "org.meveo.service.script.BullkTerminationScript",
    "description": "ExportSerenityResiliationScript",
    "type": "JAVA",
    "script": "package org.meveo.service.script;

import java.io.BufferedWriter;
import java.io.File;
import java.io.FileWriter;
import java.util.Date;
import java.util.Map;

import org.apache.commons.collections4.MapUtils;
import org.apache.commons.lang3.StringUtils;
import org.meveo.admin.exception.BusinessException;
import org.meveo.commons.utils.FlatContentBuilder;
import org.meveo.model.billing.Subscription;
import org.meveo.model.billing.SubscriptionStatusEnum;
import org.meveo.model.billing.SubscriptionTerminationReason;
import org.meveo.model.shared.DateUtils;
import org.meveo.service.billing.impl.SubscriptionService;
import org.meveo.service.billing.impl.TerminationReasonService;

public class BullkTerminationScript extends Script {

    private static final String RES_DATE_FORMAT = "dd/MM/yyyy";
    private final SubscriptionService subscriptionService = (SubscriptionService) getServiceInterface(SubscriptionService.class.getSimpleName());
    private final TerminationReasonService terminationReasonService = (TerminationReasonService) getServiceInterface(TerminationReasonService.class.getSimpleName());

    private final String CSV_SEP = ";";
    private String filePath = null;

    @SuppressWarnings("unchecked")
    @Override
    public void execute(Map<String, Object> methodContext) throws BusinessException {

        log.debug(" >>> execute {} ", methodContext.entrySet());

        try (FileWriter fileWriter = new FileWriter(filePath, true); BufferedWriter bufferedWriter = new BufferedWriter(fileWriter);) {
            String toDay = DateUtils.formatDateWithPattern(new Date(), RES_DATE_FORMAT);

            Map<String, String> record = (Map<String, String>) methodContext.get("RECORD");
            if (MapUtils.isEmpty(record)) {
                throw new BusinessException(String.format("Parameter RECORD is missing"));
            }
            String numContract = record.get("numContract");
            String reason = record.get("reason");
            String sDate = record.get("date");
            log.debug(" >>> numContract {}, reason {}, sDate {}", numContract, reason, sDate);

            String validationMsg = "";
            Subscription s = subscriptionService.findByCode(numContract);
            if (s == null) {
                validationMsg = validationMsg.concat("Unkown contrat.");
            } else if (s.getStatus() == SubscriptionStatusEnum.RESILIATED) {
                validationMsg = validationMsg.concat("Contract already terminated.");
            }
            SubscriptionTerminationReason terminationReason = terminationReasonService.findByCode(reason);
            if (terminationReason == null) {
                validationMsg = validationMsg.concat("Unkown terminationReason.");
            }
            Date terminationDate = DateUtils.parseDateWithPattern(sDate, RES_DATE_FORMAT);
            if (terminationDate == null) {
                validationMsg = validationMsg.concat("terminationDate invalid.");
            }

            FlatContentBuilder line = FlatContentBuilder.newInstance().addItem(numContract).addItem(CSV_SEP).addItem(reason).addItem(CSV_SEP).addItem(sDate).addItem(CSV_SEP);
            if (StringUtils.isNotEmpty(validationMsg)) {
                line.addItem("KO").addItem(CSV_SEP).addItem(toDay).addItem(CSV_SEP).addItem(validationMsg);
            } else {
                try {
                    s = this.subscriptionService.terminateSubscription(s, terminationDate, terminationReason, null);
                    line.addItem("OK").addItem(CSV_SEP).addItem(toDay);
                } catch (Exception e) {
                    line.addItem("KO").addItem(CSV_SEP).addItem(toDay).addItem(CSV_SEP).addItem(e.getMessage());
                }
            }
            bufferedWriter.write(line.buid());
            bufferedWriter.newLine();

        } catch (Exception e) {
            log.error(e.getMessage());
        }
    }

    @Override
    public void init(Map<String, Object> methodContext) throws BusinessException {
        try {
            log.debug(" >>> init {} ", methodContext.entrySet());

            final String originFilename = this.getContextParamAsString(methodContext, "origin_filename", true);
            final String outputDir = this.getContextParamAsString(methodContext, "outputDir", true);
            this.filePath = outputDir.concat(File.separator).concat("results_" + originFilename);

            log.debug(" >>> init -> this.filePath {} ", filePath);

        } catch (Exception e) {
            throw new BusinessException(e.getMessage());
        }
    }

    private String getContextParamAsString(Map<String, Object> methodContext, String key, boolean mandatory) throws BusinessException {
        String value = (String) methodContext.get(key);
        if (mandatory && StringUtils.isEmpty(value)) {
            throw new BusinessException(String.format("Missing context param : %s", key));
        }
        return value;
    }
}",
    "executionRoles": [],
    "sourcingRoles": []
}

