    "description": "Set trial duration on subscription",
    "type": "JAVA",
    "script": "package org.meveo.service.script;

import java.util.Map;
import java.util.Calendar;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import org.meveo.service.script.Script;
import org.meveo.admin.exception.BusinessException;

import org.meveo.model.billing.InstanceStatusEnum;
import org.meveo.model.catalog.OfferTemplate;
import org.meveo.model.catalog.ServiceTemplate;
import org.meveo.model.billing.ServiceInstance;
import org.meveo.model.billing.Subscription;
import org.meveo.model.billing.SubscriptionRenewal.RenewalPeriodUnitEnum;

import org.meveo.service.crm.impl.CustomFieldInstanceService;
import org.meveo.service.billing.impl.ServiceInstanceService;

/**

    To be triggered at TRY service activation
    Sets subscription initial term to try duration

*/
public class NotifSetTrialDuration extends Script {    
    private static final Logger log = LoggerFactory.getLogger(NotifSetTrialDuration.class);        

    private CustomFieldInstanceService cfiService = (CustomFieldInstanceService) getServiceInterface("CustomFieldInstanceService");
    private ServiceInstanceService siService = (ServiceInstanceService) getServiceInterface("ServiceInstanceService");

    @Override
    public void execute(Map<String, Object> context) throws BusinessException {
        log.info("EXECUTE context={}", context);

        ServiceInstance si = (ServiceInstance) context.get("entityOrEvent");
        if(si == null){
            throw new BusinessException("ServiceInstance is null");
        }
        log.debug("serviceInstance={}", si);

        ServiceTemplate st = (ServiceTemplate) si.getServiceTemplate();
        if(st == null){
            throw new BusinessException("ServiceTemplate is null");
        }
        log.debug("serviceTemplate={}", st);

        Subscription su = si.getSubscription();
        if(su == null){
            throw new BusinessException("Subscription is null");
        }
        log.debug("subscription={}", su);

        OfferTemplate of = su.getOffer();
        if(of == null){
            throw new BusinessException("Offer is null");
        }
        log.debug("offer={}", of);

        // Retrieve trial duration from the Try list
        String trialCode = (String)cfiService.getInheritedCFValue(si,"CF_SE_FD_TRY_CODE");
        if( trialCode == null ) {
            throw new BusinessException("Trial code (CF_SE_FD_TRY_CODE) is null for " + si);
        }

        Map trialDurationMap = (Map)cfiService.getCFValueByKey(of,"CF_OF_FD_TRY_LIST", trialCode);
        if( trialDurationMap == null ) {
            throw new BusinessException("Undefined trial in (CF_OF_FD_TRY_LIST) for code="+trialCode+" ("+of+")");
        }

        Long trialDuration = (Long)trialDurationMap.get("duration");
        if( trialDuration == null ) {
            throw new BusinessException("Undefined trial duration (CF_OF_FD_TRY_LIST) for code="+trialCode+" ("+of+")");
        }

        // Store trial duration on the subscription
        cfiService.setCFValue(su, "CF_OF_SU_FD_TRY_DURATION", trialDuration);

        if( trialDuration > 0 ) {
            Calendar endOfTrialDate = Calendar.getInstance();
            endOfTrialDate.setTime(su.getSubscriptionDate());
            endOfTrialDate.add(Calendar.DAY_OF_MONTH,trialDuration.intValue());
            log.info("Setting trial period duration for {} to {} days (endOfTrialDate={})", su, trialDuration, endOfTrialDate.getTime());

            // Update Subscription initial term and engagement
            su.getSubscriptionRenewal().setInitialyActiveForUnit(RenewalPeriodUnitEnum.DAY);
            su.getSubscriptionRenewal().setInitialyActiveFor(trialDuration.intValue());
            su.setSubscribedTillDate(endOfTrialDate.getTime());
            su.setEndAgreementDate(endOfTrialDate.getTime());
        }


/* USELESS because instantiation is now correctly ordered 1st PROMO, then mandatories
        // check for already instantiated services

        List<ServiceInstance> siList = siService.findByCodeSubscriptionAndStatus(new List<String>(), su.getCode(), InstanceStatusEnum.INACTIVE);

        siList.forEach((service)->{
            if( !service.getCode().equals(si.getCode()) ){
                Date subscriptionDate = su.getSubscriptionDate();
                Calendar endOfTrialDate = Calendar.getInstance();
                endOfTrialDate.setTime(subscriptionDate);
                endOfTrialDate.add(Calendar.MONTH,trialDuration.intValue());

                if( si.getSubscriptionDate().before(endOfTrialDate.getTime()) ) {
                    // Set activation date to end of trial date
                    si.setSubscriptionDate(endOfTrialDate.getTime());
                    log.info("Delaying {}.subscriptionDate to {}", si, endOfTrialDate.getTime());
                }
            }
        });
*/
    }
