{
    "code": "org.meveo.service.script.{{request.label}}",
    "description": "Delays discount plan activation date for trial periods",
    "type": "JAVA",
    "script": "package org.meveo.service.script;

import java.util.Map;
import java.util.Date;
import java.util.Calendar;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import org.meveo.service.script.Script;
import org.meveo.admin.exception.BusinessException;

import org.meveo.model.billing.DiscountPlanInstance;
import org.meveo.model.billing.Subscription;
import org.meveo.model.billing.BillingAccount;
import org.meveo.model.billing.UserAccount;

import org.meveo.service.crm.impl.CustomFieldInstanceService;
import org.meveo.service.billing.impl.SubscriptionService;
import org.meveo.service.billing.impl.UserAccountService;


public class NotifTrialDelayDiscountPlanActivation extends Script {
    private static final Logger log = LoggerFactory.getLogger(NotifTrialDelayDiscountPlanActivation.class);

    private CustomFieldInstanceService cfiService = (CustomFieldInstanceService) getServiceInterface("CustomFieldInstanceService");
    private SubscriptionService suService = (SubscriptionService) getServiceInterface("SubscriptionService");
    private UserAccountService uaService = (UserAccountService) getServiceInterface("UserAccountService");

    @Override
    public void execute(Map<String, Object> context) throws BusinessException {
        log.info("EXECUTE context={}", context);

        DiscountPlanInstance di = (DiscountPlanInstance) context.get("entityOrEvent");
        if(di == null){
            throw new BusinessException("DiscountPlanInstance is null");
        }
        log.trace("discountPlanInstance={}", di);

        BillingAccount ba = (BillingAccount)di.getBillingAccount();
        if(ba == null){
            throw new BusinessException("billingAccount is null for "+di);
        }
        log.trace("{}", ba);

        UserAccount ua = (UserAccount)uaService.findByCode(ba.getCode());
        if(ua == null){
            throw new BusinessException("userAccount is null for "+ba);
        }
        log.trace("{}", ua);

        Subscription su = null;
        try {
            su = (Subscription)suService.listByUserAccount(ua).get(0);
        } catch(Exception e) {

        }

        if(su == null){
            throw new BusinessException("subscription not found for "+ua);
        }
        log.trace("{}", su);

        // Compute EndOfTrial date
        Long trialDuration = (Long)cfiService.getInheritedCFValue(su,"CF_OF_SU_FD_TRY_DURATION");
        Date subscriptionDate = su.getSubscriptionDate();
        Calendar endOfTrialDate = Calendar.getInstance();
        endOfTrialDate.setTime(subscriptionDate);
        endOfTrialDate.add(Calendar.DAY_OF_MONTH,trialDuration.intValue());


        if( di.getStartDate() != null && di.getStartDate().before(endOfTrialDate.getTime()) ) {
            // Set activation date to end of trial date
            di.setStartDate(endOfTrialDate.getTime());

            // Compute discount duration 
            if( di.getEndDate() != null ) {
                Long discountDuration = di.getEndDate().getTime() - di.getStartDate().getTime();
                di.setEndDate(new Date(di.getStartDate().getTime()+discountDuration));
            }

            log.info("Delaying discountPlanInstance={} on {} to [{};{}[", di, ba, di.getStartDate(), di.getEndDate());
        }
    }
}
",
    "executionRoles": [],
    "sourcingRoles": []
}

